DROP DATABASE IF EXISTS `soulTable`;

CREATE DATABASE  `soulTable` DEFAULT CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci;

SET NAMES utf8mb4;

USE `soulTable`;

DROP TABLE IF EXISTS `poetry`;
CREATE TABLE `poetry` (
                        `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '自增id',
                        `title` varchar(50) DEFAULT NULL COMMENT '诗词',
                        `dynasty` varchar(50) DEFAULT NULL COMMENT '朝代',
                        `author` varchar(50) DEFAULT NULL COMMENT '作者',
                        `content` varchar(500) DEFAULT NULL COMMENT '内容',
                        `type` varchar(50) DEFAULT NULL COMMENT '类型',
                        `heat` int(11) NOT NULL DEFAULT '0' COMMENT '点赞数',
                        `create_time` datetime DEFAULT CURRENT_TIMESTAMP COMMENT '录入时间',
                        PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=985 DEFAULT CHARSET=utf8 COMMENT='诗词表';
BEGIN;
INSERT INTO `poetry` VALUES (1,'简兮','先秦','佚名','简兮简兮，方将万舞。\n日之方中，在前上处。\n硕人俣俣，公庭万舞。\n有力如虎，执辔如组。\n左手执龠，右手秉翟。\n赫如渥赭，公言锡爵。\n山有榛，隰有苓。\n云谁之思？西方美人。\n彼美人兮，西方之人兮。','诗经,写人',174,'2019-03-01 22:54:40'),(2,'菩萨蛮·春波软荡红楼水','清代','吴锡麒','春波软荡红楼水，多时不放莺儿起。\n一样夕阳天，留寒待禁烟。\n已是人消瘦，只此情依旧。\n可奈别离何，明朝杨柳多。','婉约,写景,思念',14,'2019-03-01 22:54:40'),(3,'点绛唇·醉漾轻舟','宋代','秦观','醉漾轻舟，信流引到花深处。\n尘缘相误，无计花间住。\n烟水茫茫，千里斜阳暮。\n山无数，乱红如雨。\n不记来时路。','写景,抒情,山水',115,'2019-03-01 22:54:41'),(4,'水龙吟·夜来风雨匆匆','宋代','程垓','夜来风雨匆匆，故园定是花无几。\n愁多怨极，等闲孤负，一年芳意。\n柳困桃慵，杏青梅小，对人容易。\n算好春长在，好花长见，原只是、人憔悴。\n回首池南旧事，恨星星、不堪重记。\n如今但有，看花老眼，伤时清泪。\n不怕逢花瘦，只愁怕、老来风味。\n待繁红乱处，留云借月，也须拚醉。','追忆,思乡,伤怀,感慨',20,'2019-03-01 22:54:41'),(5,'太常引·钱齐参议归山东','元代','刘燕哥','故人送我出阳关，无计锁雕鞍。\n今古别离难。\n兀谁画娥眉远山。\n一尊别酒，一声杜宇，寂寞又春残。\n明月小楼间，第一夜相思泪弹。','婉约,送别,惜别,相思',13,'2019-03-01 22:54:41'),(6,'春日独酌二首','唐代','李白','东风扇淑气， 水木荣春晖。\n白日照绿草， 落花散且飞。\n孤云还空山， 众鸟各已归。\n彼物皆有讬， 吾生独无依。\n对此石上月， 长歌醉芳菲。\n\n我有紫霞想，缅怀沧州间。\n且对一壶酒，澹然万事闲。\n横琴倚高松，把酒望远山。\n长空去鸟没，落日孤云还。\n但悲光景晚，宿昔成秋颜。','春天,写景,抒情,孤寂',20,'2019-03-01 22:54:41'),(7,'望江南·幽州九日','宋代','汪元量','官舍悄，坐到月西斜。\n永夜角声悲自语，客心愁破正思家。\n南北各天涯。\n肠断裂，搔首一长嗟。\n绮席象床寒玉枕，美人何处醉黄花。\n和泪捻琵琶。','宋词精选,重阳节,思念',12,'2019-03-01 22:54:41'),(8,'戏题王宰画山水图歌','唐代','杜甫','十日画一水，五日画一石。\n能事不受相促迫，王宰始肯留真迹。\n壮哉昆仑方壶图，挂君高堂之素壁。\n巴陵洞庭日本东，赤岸水与银河通，中有云气随飞龙。\n舟人渔子入浦溆，山木尽亚洪涛风。\n尤工远势古莫比，咫尺应须论万里。\n焉得并州快剪刀，剪取吴淞半江水。','写人,赞美',6,'2019-03-01 22:54:41'),(9,'早发定山','南北朝','沈约','夙龄爱远壑，晚莅见奇山。\n标峰彩虹外，置岭白云间。\n倾壁忽斜竖，绝顶复孤圆。\n归海流漫漫，出浦水溅溅。\n野棠开未落，山樱发欲然。\n忘归属兰杜，怀禄寄芳荃。\n眷言采三秀，徘徊望九仙。','写景,山水',1,'2019-03-01 22:54:41'),(10,'壬辰寒食','宋代','王安石','客思似杨柳，春风千万条。\n更倾寒食泪，欲涨冶城潮。\n巾发雪争出，镜颜朱早凋。\n未知轩冕乐，但欲老渔樵。','寒食节,感伤,思乡',25,'2019-03-01 22:54:41'),(11,'渡江云三犯·西湖清明','宋代','吴文英','羞红颦浅恨，晚风未落，片绣点重茵。\n旧堤分燕尾，桂棹轻鸥，宝勒倚残云。\n千丝怨碧，渐路入、仙坞迷津。\n肠漫回，隔花时见，背面楚腰身。\n 逡巡。\n题门惆怅，堕履牵萦，数幽期难准。\n还始觉、留情缘眼，宽带因春。\n明朝事与孤烟冷，做满湖、风雨愁人。\n山黛暝，尘波澹绿无痕。','清明节,写景,追忆',212,'2019-03-01 22:54:41'),(12,'重有感','唐代','李商隐','玉帐牙旗得上游，安危须共主君忧。\n窦融表已来关右，陶侃军宜次石头。\n岂有蛟龙愁失水，更无鹰隼与高秋！昼号夜哭兼幽显，早晚星关雪涕收？','爱国,忧国',22,'2019-03-01 22:54:41'),(13,'送龚章判官之卫州·新及第','宋代','司马光','几砚昔年游，于今成十秋。\n松坚终发石，鱼变即辞流。\n近郡无飞檄，清时不借筹。\n淇园春竹美，军宴日椎牛。','送别,忆旧,友情,勉励',10,'2019-03-01 22:54:41'),(14,'旅夜书怀','唐代','杜甫','细草微风岸，危樯独夜舟。\n星垂平野阔，月涌大江流。\n名岂文章著，官应老病休。\n飘飘何所似，天地一沙鸥。','唐诗三百首,写景,抒怀,孤独,最美',1400,'2019-03-01 22:54:41'),(15,'奉试明堂火珠','唐代','崔曙','正位开重屋，凌空出火珠。\n夜来双月满，曙后一星孤。\n天净光难灭，云生望欲无。\n遥知太平代，国宝在名都。','咏物,赞美',6,'2019-03-01 22:54:41'),(16,'采莲曲','唐代','白居易','菱叶萦波荷飐风，荷花深处小船通。\n 逢郎欲语低头笑，碧玉搔头落水中。','荷花,女子,爱情',709,'2019-03-01 22:54:42'),(17,'长相思·其一','唐代','李白','长相思，在长安。\n络纬秋啼金井阑，微霜凄凄簟色寒。\n孤灯不明思欲绝，卷帷望月空长叹。\n美人如花隔云端！上有青冥之长天，下有渌水之波澜。\n天长路远魂飞苦，梦魂不到关山难。\n长相思，摧心肝！','唐诗三百首,乐府,思念,伤怀',1589,'2019-03-01 22:54:42'),(18,'国风·邶风·旄丘','先秦','佚名','旄丘之葛兮，何诞之节兮。\n叔兮伯兮，何多日也？何其处也？必有与也！何其久也？必有以也！狐裘蒙戎，匪车不东。\n叔兮伯兮，靡所与同。\n琐兮尾兮，流离之子。\n叔兮伯兮，褎如充耳。','诗经',79,'2019-03-01 22:54:42'),(19,'狱中对月','明代','宋琬','疏星耿耿逼人寒，清漏丁丁画角残。\n客泪久从悉外尽，月明犹许醉中看。\n栖乌绕树冰霜苦，哀雁横天关塞难。\n料得故园今夜梦，随风应已到长安。','怀人,抒情,愤懑',4,'2019-03-01 22:54:42'),(20,'落梅','宋代','刘克庄','一片能教一断肠，可堪平砌更堆墙。\n飘如迁客来过岭，坠似骚人去赴湘。\n乱点莓苔多莫数，偶粘衣袖久犹香。\n东风谬掌花权柄，却忌孤高不主张。','古诗三百首,咏物,梅花,品格,抒怀',61,'2019-03-01 22:54:42'),(21,'龙兴寺吊少陵先生寓居','宋代','陆游','中原草草失承平，戍火胡尘到两京。\n扈跸老臣身万里，天寒来此听江声！','怀古,凭吊,感慨',23,'2019-03-01 22:54:42'),(22,'一毛不拔','魏晋','邯郸淳','一猴死，见冥王，求转人身。\n王曰：“既欲做人，须将毛尽拔去。\n”即唤夜叉拔之。\n方拔一根，猴不胜痛叫。\n王笑曰：“看你一毛不拔，如何做人？”','小学文言文,寓言,故事,哲理',869,'2019-03-01 22:54:42'),(23,'满江红·屈指兴亡','明代','张煌言','屈指兴亡，恨南北、黄图消歇。\n便几个、孤忠大义，冰清玉烈。\n赵信城边羌笛雨，李陵台上胡笳月。\n惨模糊、吹出玉关情，声凄切。\n汉宫露，梁园雪。\n双龙逝，一鸿灭。\n剩逋臣怒击，唾壶皆缺。\n豪杰气吞白凤髓，高怀眦饮黄羊血。\n试排云、待把捧日心，诉金阙。','抒怀,述志,亡国',9,'2019-03-01 22:54:42'),(24,'菩萨蛮·人人尽说江南好','唐代','韦庄','人人尽说江南好，游人只合江南老。\n春水碧于天，画船听雨眠。\n垆边人似月，皓腕凝霜雪。\n未老莫还乡，还乡须断肠。','宋词三百首,婉约,江南,思乡,羁旅',996,'2019-03-01 22:54:42'),(25,'点绛唇·素香丁香','宋代','王十朋','落木萧萧，琉璃叶下琼葩吐。\n素香柔树。\n雅称幽人趣。\n 无意争先，梅蕊休相妒。\n含春雨。\n结愁千绪。\n似忆江南主。','咏物,写花,言志',62,'2019-03-01 22:54:42'),(26,'江城子·浣花溪上见卿卿','唐代','张泌','浣花溪上见卿卿，脸波明，黛眉轻。\n绿云高绾，金簇小蜻蜒。\n好是问他来得么？和道：莫多情。','爱情,约会',21,'2019-03-01 22:54:42'),(27,'庆东原·西皋亭适兴','元代','薛昂夫','兴为催租败，欢因送酒来。\n酒酣时诗兴依然在。\n黄花又开，朱颜未衰，正好忘怀。\n管甚有监州，不可无螃蟹。','节日,重阳节,抒怀,人生',7,'2019-03-01 22:54:42'),(28,'山坡羊·潼关怀古','元代','张养浩','峰峦如聚，波涛如怒，山河表里潼关路。\n望西都，意踌躇。\n伤心秦汉经行处，宫阙万间都做了土。\n兴，百姓苦；亡，百姓苦！','初中古诗,高中古诗,怀古,同情,忧民,最美',3116,'2019-03-01 22:54:43'),(29,'蓦山溪·题钱氏溪月','宋代','姜夔','与鸥为客，绿野留吟屐。\n两行柳垂阴，是当日、仙翁手植。\n一亭寂寞，烟外带愁横。\n荷苒苒，展凉云，横卧虹千尺。\n才因老尽，秀句君休觅。\n万绿正迷人，更愁入、山阳夜笛。\n百年心事，惟有玉阑知，吟未了，放船回，月下空相忆。','友人,思念,怀旧',8,'2019-03-01 22:54:43'),(30,'送顿起','宋代','苏轼','客路相逢难，为乐常不足。\n临行挽衫袖，更尝折残菊。\n酒阑不忍去，共接一寸烛。\n留君终无穷，归驾不免促。\n岱宗已在眼，一往继前躅。\n佳人亦何念，凄断阳关曲。\n天门四十里，夜看扶桑浴。\n回头望彭城，大海浮一粟。\n故人在其下，尘土相豗蹴。\n惟有黄楼诗，千古配淇澳。','离别,感叹,人生,友情',12,'2019-03-01 22:54:43'),(31,'念奴娇·中秋对月','明代','文征明','桂花浮玉，正月满天街，夜凉如洗。\n风泛须眉并骨寒，人在水晶宫里。\n蛟龙偃蹇，观阙嵯峨，缥缈笙歌沸。\n霜华满地，欲跨彩云飞起。\n记得去年今夕，酾酒溪亭，淡月云来去。\n千里江山昨梦非，转眼秋光如许。\n青雀西来，嫦娥报我，道佳期近矣。\n寄言俦侣，莫负广寒沈醉。','中秋节,月下,感伤,想象',83,'2019-03-01 22:54:43'),(32,'虢国夫人夜游图','宋代','苏轼','佳人自鞚玉花骢，翩如惊燕蹋飞龙。\n金鞭争道宝钗落，何人先入明光宫。\n宫中羯鼓催花柳，玉奴弦索花奴手。\n坐中八姨真贵人，走马来看不动尘。\n明眸皓齿谁复见，只有丹青余泪痕。\n人间俯仰成今古，吴公台下雷塘路。\n当时亦笑张丽华，不知门外韩擒虎。','咏史怀古,写人,讽刺',16,'2019-03-01 22:54:43'),(33,'清平乐·瓜洲渡口','宋代','李好古','瓜州渡口。\n恰恰城如斗。\n乱絮飞钱迎马首。\n也学玉关榆柳。\n 面前直控金山。\n极知形胜东南。\n更愿诸公著意，休教忘了中原。','婉约,写景,抒情,议论',6,'2019-03-01 22:54:43'),(34,'折荷有赠','唐代','李白','涉江玩秋水，爱此红蕖鲜。\n攀荷弄其珠，荡漾不成圆。\n佳人彩云里，欲赠隔远天。\n相思无因见，怅望凉风前。','荷花,言志,惆怅',125,'2019-03-01 22:54:43'),(35,'送客归京','唐代','白居易','水陆四千里，何时归到秦？舟辞三峡雨，马入九衢尘。\n有酒留行客，无书寄贵人。\n唯凭远传语，好在曲江春。','送别,友人',14,'2019-03-01 22:54:43'),(36,'祈父','先秦','佚名','祈父，予王之爪牙。\n胡转予于恤，靡所止居？祈父，予王之爪士。\n胡转予于恤，靡所厎止？祈父，亶不聪。\n胡转予于恤？有母之尸饔。','诗经,抒情',44,'2019-03-01 22:54:43'),(37,'送白少府送兵之陇右','唐代','高适','践更登陇首，远别指临洮。\n为问关山事，何如州县劳。\n军容随赤羽，树色引青袍。\n谁断单于臂，今年太白高。','军队,战争',21,'2019-03-01 22:54:43'),(38,'国风·豳风·破斧','先秦','佚名','既破我斧，又缺我斨。\n周公东征，四国是皇。\n哀我人斯，亦孔之将。\n既破我斧，又缺我锜。\n周公东征，四国是吪。\n哀我人斯，亦孔之嘉。\n既破我斧，又缺我銶。\n周公东征，四国是遒。\n哀我人斯，亦孔之休。','诗经,赞美',58,'2019-03-01 22:54:43'),(39,'苏溪亭','唐代','戴叔伦','苏溪亭上草漫漫，谁倚东风十二阑。\n 燕子不归春事晚，一汀烟雨杏花寒。','春天,闺怨,思念',282,'2019-03-01 22:54:43'),(40,'读诸葛武侯传书怀赠长安崔少府叔封昆季','唐代','李白','汉道昔云季，群雄方战争。\n霸图各未立，割据资豪英。\n赤伏起颓运，卧龙得孔明。\n当其南阳时，陇亩躬自耕。\n鱼水三顾合，风云四海生。\n武侯立岷蜀，壮志吞咸京。\n何人先见许，但有崔州平。\n余亦草间人，颇怀拯物情。\n晚途值子玉，华发同衰荣。\n托意在经济，结交为弟兄。\n毋令管与鲍，千载独知名。','抱负',32,'2019-03-01 22:54:43'),(41,'菩萨蛮·夜来皓月才当午','唐代','温庭筠','夜来皓月才当午，重帘悄悄无人语。\n深处麝烟长，卧时留薄妆。\n当年还自惜，往事那堪忆。\n花露月明残，锦衾知晓寒。','女子,相思,抒情,愁绪',5,'2019-03-01 22:54:44'),(42,'题大禹寺义公禅房','唐代','孟浩然','义公习禅寂，结宇依空林。\n户外一峰秀，阶前众壑深。\n夕阳连雨足，空翠落庭阴。\n看取莲花净，应知不染心。','山水,赞美',18,'2019-03-01 22:54:44'),(43,'一落索·眉共春山争秀','宋代','周邦彦','眉共春山争秀。\n可怜长皱。\n莫将清泪湿花枝，恐花也、如人瘦。\n清润玉箫闲久。\n知音稀有，欲知日日倚阑愁，但问取、亭前柳。','写人,少女,相思',31,'2019-03-01 22:54:44'),(44,'代秋情','唐代','李白','几日相别离，门前生穞葵。\n寒蝉聒梧桐，日夕长鸣悲。\n白露湿萤火，清霜凌兔丝。\n空掩紫罗袂，长啼无尽时。','秋天,写景,抒情,离别,悲伤',8,'2019-03-01 22:54:44'),(45,'客中除夕','明代','袁凯','今夕为何夕，他乡说故乡。\n看人儿女大，为客岁年长。\n戎马无休歇，关山正渺茫。\n一杯柏叶酒，未敌泪千行。','春节,节日,乡情',76,'2019-03-01 22:54:44'),(46,'再上湘江','唐代','柳宗元','好在湘江水，今朝又上来。\n不知从此去，更遣几年回。','写江,感慨,愁苦',26,'2019-03-01 22:54:44'),(47,'满江红·点火樱桃','宋代','辛弃疾','点火樱桃，照一架、荼蘼如雪。\n春正好，见龙孙穿破，紫苔苍壁。\n乳燕引雏飞力弱，流莺唤友娇声怯。\n问春归、不肯带愁归，肠千结。\n层楼望，春山叠；家何在？烟波隔。\n把古今遗恨，向他谁说？蝴蝶不传千里梦，子规叫断三更月。\n听声声、枕上劝人归，归难得。','写景,伤春,爱国,思归',91,'2019-03-01 22:54:44'),(48,'汾沮洳','先秦','佚名','彼汾沮洳，言采其莫。\n彼其之子，美无度。\n美无度，殊异乎公路。\n彼汾一方，言采其桑。\n彼其之子，美如英。\n美如英，殊异乎公行。\n彼汾一曲，言采其藚。\n彼其之子，美如玉。\n美如玉，殊异乎公族。','诗经,赞美,写人',74,'2019-03-01 22:54:44'),(49,'减字木兰花·送东武令赵昶失官归海州','宋代','苏轼','贤哉令尹，三仕已之无喜愠。\n我独何人，犹把虚名玷缙绅。\n不如归去，二顷良田无觅处。\n归去来兮，待有良田是几时。','赠别,慰勉',14,'2019-03-01 22:54:44'),(50,'秋日鲁郡尧祠亭上宴别杜补阙范侍御','唐代','李白','我觉秋兴逸，谁云秋兴悲？山将落日去，水与晴空宜。\n鲁酒白玉壶，送行驻金羁。\n歇鞍憩古木，解带挂横枝。\n歌鼓川上亭，曲度神飙吹。\n云归碧海夕，雁没青天时。\n相失各万里，茫然空尔思。','秋天,送别,友人,抒情,豪放',20,'2019-03-01 22:54:44'),(51,'水仙子·西湖探梅','元代','杨朝英','雪晴天地一冰壶，竟往西湖探老逋，骑驴踏雪溪桥路。\n笑王维作画图，拣梅花多处提壶。\n对酒看花笑，无钱当剑沽，醉倒在西湖！','写雪,写花,梅花,抒情',4,'2019-03-01 22:54:44'),(52,'霜花腴·重阳前一日泛石湖','宋代','吴文英','翠微路窄，醉晚风、凭谁为整欹冠。\n霜饱花腴，烛消人瘦，秋光作也都难。\n病怀强宽。\n恨雁声、偏落歌前。\n记年时、旧宿凄凉，暮烟秋雨野桥寒。\n 妆靥鬓英争艳，度清商一曲，暗坠金蝉。\n芳节多阴，兰情稀会，晴晖称拂吟笺。\n更移画船。\n引佩环、邀下婵娟。\n算明朝、未了重阳，紫萸应耐看。','重阳节,登高,回忆',8,'2019-03-01 22:54:45'),(53,'出关宿盘豆馆对丛芦有感','唐代','李商隐','芦叶梢梢夏景深，邮亭暂欲洒尘襟。\n昔年曾是江南客，此日初为关外心。\n思子台边风自急，玉娘湖上月应沉。\n清声不远行人去，一世荒城伴夜砧。','羁旅,抒怀,思念',3,'2019-03-01 22:54:45'),(54,'鹧鸪天·祖国沉沦感不禁','近现代','秋瑾','祖国沉沦感不禁，闲来海外觅知音。\n金瓯已缺总须补，为国牺牲敢惜身！嗟险阻，叹飘零。\n关山万里作雄行。\n休言女子非英物，夜夜龙泉壁上鸣。','豪放,忧国,爱国',96,'2019-03-01 22:54:45'),(55,'贺新郎·送胡邦衡待制赴新州','宋代','张元干','梦绕神州路。\n怅秋风、连营画角，故宫离黍。\n底事昆仑倾砥柱，九地黄流乱注。\n聚万落千村狐兔。\n天意从来高难问，况人情老易悲难诉。\n更南浦，送君去。\n凉生岸柳催残暑。\n耿斜河，疏星残月，断云微度。\n万里江山知何处？回首对床夜语。\n雁不到，书成谁与？目尽青天怀今古，肯儿曹恩怨相尔汝！举大白，听《金缕》。','宋词三百首,豪放,送别,忧国忧民,爱国',152,'2019-03-01 22:54:45'),(56,'隰桑','先秦','佚名','隰桑有阿，其叶有难。\n既见君子，其乐如何。\n\n隰桑有阿，其叶有沃。\n既见君子，云何不乐。\n\n隰桑有阿，其叶有幽。\n既见君子，德音孔胶。\n\n心乎爱矣，遐不谓矣？中心藏之，何日忘之！','诗经,爱情',82,'2019-03-01 22:54:45'),(57,'庭前菊','唐代','韦庄','为忆长安烂熳开，我今移尔满庭栽。\n 红兰莫笑青青色，曾向龙山泛酒来。','咏物,菊花,思乡',40,'2019-03-01 22:54:45'),(58,'东门之墠','先秦','佚名','东门之墠，茹藘在阪。\n其室则迩，其人甚远。\n东门之栗，有践家室。\n岂不尔思？子不我即！','诗经,爱情',41,'2019-03-01 22:54:45'),(59,'对酒忆贺监二首','唐代','李白','四明有狂客，风流贺季真。\n长安一相见，呼我谪仙人。\n昔好杯中物，翻为松下尘。\n金龟换酒处，却忆泪沾巾。\n\n狂客归四明，山阴道士迎。\n敕赐镜湖水，为君台沼荣。\n人亡余故宅，空有荷花生。\n念此杳如梦，凄然伤我情。','悼念',38,'2019-03-01 22:54:45'),(60,'清平乐·留人不住','宋代','晏几道','留人不住，醉解兰舟去。\n一棹碧涛春水路，过尽晓莺啼处。\n渡头杨柳青青，枝枝叶叶离情。\n此后锦书休寄，画楼云雨无凭。','宋词三百首,离别,离情,柳树,分手',287,'2019-03-01 22:54:45'),(61,'念奴娇·过洞庭','宋代','张孝祥','洞庭青草，近中秋，更无一点风色。\n玉界琼田三万顷，着我扁舟一叶。\n素月分辉，明河共影，表里俱澄澈。\n悠然心会，妙处难与君说。\n(著 同：着；玉界 一作：玉鉴)应念岭表经年，孤光自照，肝胆皆冰雪。\n短发萧骚襟袖冷，稳泛沧浪空阔。\n尽挹西江，细斟北斗，万象为宾客。\n扣舷独啸，不知今夕何夕。\n(沧浪 一作：沧冥；岭表 一作：岭海)','宋词三百首,豪放,写景,写水,月夜,抒怀',545,'2019-03-01 22:54:45'),(62,'边词','唐代','张敬忠','五原春色旧来迟，二月垂杨未挂丝。\n (旧来迟 一作：归来迟)即今河畔冰开日，正是长安花落时。','边塞,春天',93,'2019-03-01 22:54:45'),(63,'八六子·倚危亭','宋代','秦观','倚危亭。\n恨如芳草，萋萋刬尽还生。\n念柳外青骢别后，水边红袂分时，怆然暗惊。\n无端天与娉婷。\n夜月一帘幽梦，春风十里柔情。\n怎奈向、欢娱渐随流水，素弦声断，翠绡香减，那堪片片飞花弄晚，蒙蒙残雨笼晴。\n正销凝。\n黄鹂又啼数声。','宋词三百首,离别,离情,爱情,怀人',236,'2019-03-01 22:54:45'),(64,'吾富有钱时','唐代','王梵志','吾富有钱时，妇儿看我好。\n吾若脱衣裳，与吾叠袍袄。\n吾出经求去，送吾即上道。\n将钱入舍来，见吾满面笑。\n绕吾白鸽旋，恰似鹦鹉鸟。\n邂逅暂时贫，看吾即貌哨。\n人有七贫时，七富还相报。\n图财不顾人，且看来时道。','写人,感慨',3,'2019-03-01 22:54:45'),(65,'在金日作·选二','宋代','宇文虚中','满腹诗书漫古今，频年流落易伤心。\n南冠终日囚军府，北雁何时到上林？开口摧颓空抱朴，协肩奔走尚腰金。\n莫邪利剑今何在？不斩奸邪恨最深！\n遥夜沉沉满幕霜，有时归梦到家乡。\n传闻已筑西河馆，自许能肥北海羊。\n回首两朝俱草莽，驰心万里绝农桑。\n人生一死浑闲事，裂眥穿胸不汝忘！','爱国,伤怀',12,'2019-03-01 22:54:45'),(66,'送李中丞之襄州 / 送李中丞归汉阳别业','唐代','刘长卿','流落征南将，曾驱十万师。\n罢归无旧业，老去恋明时。\n独立三边静，轻生一剑知。\n茫茫江汉上，日暮欲何之。','送别,离别,同情,以景衬情',32,'2019-03-01 22:54:46'),(67,'渔家傲·和程公辟赠','宋代','张先','巴子城头青草暮，巴山重叠相逢处。\n燕子占巢花脱树。\n杯且举，瞿塘水阔舟难渡。\n天外吴门清霅路。\n君家正在吴门住。\n赠我柳枝情几许。\n春满缕，为君将入江南去。','赠别,友情,写景',11,'2019-03-01 22:54:46'),(68,'寄赠薛涛','唐代','元稹','锦江滑腻蛾眉秀，幻出文君与薛涛。\n言语巧偷鹦鹉舌，文章分得凤凰毛。\n纷纷辞客多停笔，个个公卿欲梦刀。\n别后相思隔烟水，菖蒲花发五云高。','写人,才女',22,'2019-03-01 22:54:46'),(69,'凤栖梧·绿暗红稀春已暮','宋代','葛长庚','绿暗红稀春已暮，燕子衔泥，飞入垂杨处。\n柳絮欲停风不住，杜鹃声里山无数。\n竹杖芒鞋无定据，穿过溪南，独木横桥路。\n樵子渔师来又去，一川风月谁为主。','婉约,游子,羁旅,忧愁',13,'2019-03-01 22:54:46'),(70,'咸阳城东楼 / 咸阳城西楼晚眺','唐代','许浑','一上高城万里愁，蒹葭杨柳似汀洲。\n溪云初起日沉阁，山雨欲来风满楼。\n(沉 通：沈)鸟下绿芜秦苑夕，蝉鸣黄叶汉宫秋。\n行人莫问当年事，故国东来渭水流。','写景,吊古伤今,优美',516,'2019-03-01 22:54:46'),(71,'秋月','宋代','朱熹','清溪流过碧山头，空水澄鲜一色秋。\n隔断红尘三十里，白云红叶两悠悠。','写景,抒情,闲适',3,'2019-03-01 22:54:46'),(72,'江城子·鵁鶄飞起郡城东','唐代','牛峤','鵁鶄飞起郡城东。\n碧江空，半滩风。\n越王宫殿，蘋叶藕花中。\n帘卷水楼鱼浪起，千片雪，雨蒙蒙。','怀古,感慨,人生',2,'2019-03-01 22:54:46'),(73,'早梅','唐代','齐己','万木冻欲折，孤根暖独回。\n前村深雪里，昨夜一枝开。\n风递幽香出，禽窥素艳来。\n明年如应律，先发望春台。','咏物,梅花,言志',306,'2019-03-01 22:54:46'),(74,'生查子·惆怅彩云飞','清代','纳兰性德','惆怅彩云飞，碧落知何许。\n不见合欢花，空倚相思树。\n总是别时情，那待分明语。\n判得最长宵，数尽厌厌雨。','悼亡,追忆,哀伤',46,'2019-03-01 22:54:46'),(75,'齐天乐·上元','清代','纳兰性德','阑珊火树鱼龙舞，望中宝钗楼远。\n鞣鞠余红，琉璃剩碧，待属花归缓缓。\n寒轻漏浅。\n正乍敛烟霏，陨星如箭。\n旧事惊心，一双莲影藕丝断。\n莫恨流年似水，恨消残蝶粉，韶光忒浅。\n细语吹香，暗尘笼撰，都逐晓风零乱。\n阑干敲遍。\n问帘底纤纤，甚时重见？不解相思，月华今夜满。','节日,抒情,怀人',3,'2019-03-01 22:54:46'),(76,'临江仙·幽闺欲曙闻莺转','五代','毛熙震','幽闺欲曙闻莺转，红窗月影微明。\n好风频谢落花声。\n隔帷残烛，犹照绮屏筝。\n绣被锦茵眠玉暖，炷香斜袅烟轻。\n淡蛾羞敛不胜情。\n暗思闲梦，何处逐云行？','女子,抒情,思念,惆怅',5,'2019-03-01 22:54:46'),(77,'玉漏迟·咏杯','金朝','元好问','淅江归路杳，西南却羡、投林高鸟。\n升斗微官，世累苦相萦绕。\n不似麒麟殿里，又不与、巢由同调。\n时自笑，虚名负我，半生吟啸。\n扰扰马足车尘，被岁月无情，暗消年少。\n钟鼎山林，一事几时曾了。\n四壁秋虫夜雨，更一点、残灯斜照。\n清镜晓，白发又添多少。','婉约,抒怀,感叹',19,'2019-03-01 22:54:46'),(78,'汉江临泛 / 汉江临眺','唐代','王维','楚塞三湘接，荆门九派通。\n江流天地外，山色有无中。\n郡邑浮前浦，波澜动远空。\n襄阳好风日，留醉与山翁。','唐诗三百首,山水,抒情',412,'2019-03-01 22:54:46'),(79,'丹阳送韦参军','唐代','严维','丹阳郭里送行舟，一别心知两地秋。\n 日晚江南望江北，寒鸦飞尽水悠悠。','送别,思念,友情',117,'2019-03-01 22:54:47'),(80,'赠刘司户蕡','唐代','李商隐','江风扬浪动云根，重碇危樯白日昏。\n已断燕鸿初起势，更惊骚客后归魂。\n汉廷急诏谁先入，楚路高歌自欲翻。\n万里相逢欢复泣，凤巢西隔九重门。','赠友,抒怀,忧国',21,'2019-03-01 22:54:47'),(81,'牧童词','唐代','张籍','远牧牛，绕村四面禾黍稠。\n陂中饥乌啄牛背，令我不得戏垄头。\n入陂草多牛散行，白犊时向芦中鸣。\n隔堤吹叶应同伴，还鼓长鞭三四声。\n牛牛食草莫相触，官家截尔头上角。','写人,抒情,揭露,社会,黑暗',21,'2019-03-01 22:54:47'),(82,'国风·邶风·旄丘','先秦','佚名','旄丘之葛兮，何诞之节兮。\n叔兮伯兮，何多日也？何其处也？必有与也！何其久也？必有以也！狐裘蒙戎，匪车不东。\n叔兮伯兮，靡所与同。\n琐兮尾兮，流离之子。\n叔兮伯兮，褎如充耳。','诗经',79,'2019-03-01 22:54:47'),(83,'和子由踏青','宋代','苏轼','东风陌上惊微尘，游人初乐岁华新。\n人闲正好路旁饮，麦短未怕游车轮。\n城中居人厌城郭，喧阗晓出空四邻。\n歌鼓惊山草木动，箪瓢散野乌鸢驯。\n何人聚众称道人？遮道卖符色怒嗔：宜蚕使汝茧如瓮，宜畜使汝羊如麇。\n路人未必信此语，强为买服禳新春。\n道人得钱径沽酒，醉倒自谓吾符神！','回忆,思乡,怀人',7,'2019-03-01 22:54:47'),(84,'迎春乐·菖蒲叶叶知多少','宋代','秦观','菖蒲叶叶知多少，惟有个、蜂儿妙。\n雨晴红粉齐开了，露一点、娇黄小。\n早是被、晓风力暴，更春共、斜阳俱老。\n怎得香香深处，作个蜂儿抱。','写景,爱情',4,'2019-03-01 22:54:47'),(85,'奉答李和甫代简二绝句','宋代','黄庭坚','山色江声相与清，卷帘待得月华生。\n可怜一曲并船笛，说尽故人离别情。\n\n梦中往事随心见，醉里繁华乱眼生。\n长为风流恼人病，不如天性总无情。','思念,友人,抒情,苦闷',18,'2019-03-01 22:54:47'),(86,'边词','唐代','张敬忠','五原春色旧来迟，二月垂杨未挂丝。\n (旧来迟 一作：归来迟)即今河畔冰开日，正是长安花落时。','边塞,春天',93,'2019-03-01 22:54:47'),(87,'读山海经十三首·其九','魏晋','陶渊明','夸父诞宏志，乃与日竞走。\n俱至虞渊下，似若无胜负。\n神力既殊妙，倾河焉足有！馀迹寄邓林，功竟在身后。','神话,故事,歌颂',2,'2019-03-01 22:54:47'),(88,'残菊','清代','曹雪芹','露凝霜重渐倾欹，宴赏才过小雪时。\n蒂有余香金淡泊，枝无全叶翠离披。\n半床落月蛩声病，万里寒云雁阵迟。\n明岁秋风知再会，暂时分手莫相思。','写花,菊花,寓人',71,'2019-03-01 22:54:47'),(89,'浣溪沙·缥缈红妆照浅溪','宋代','苏轼','缥缈红妆照浅溪。\n薄云疏雨不成泥。\n送君何处古台西。\n废沼夜来秋水满，茂林深处晚莺啼。\n行人肠断草凄迷。','写景,送别',207,'2019-03-01 22:54:47'),(90,'念奴娇·天南地北','元代','施耐庵','天南地北，问乾坤，何处可容狂客？借得山东烟水寨，来买凤城春色。\n翠袖围香，绛绡笼雪，一笑千金值。\n神仙体态，薄幸如何消得？想芦叶滩头，蓼花汀畔，皓月空凝碧。\n六六雁行连八九，只待金鸡消息。\n义胆包天，忠肝盖地，四海无人识。\n离愁万种，醉乡一夜头白。','女子,理想',136,'2019-03-01 22:54:47'),(91,'怨歌行','两汉','曹植','为君既不易，为臣良独难。\n忠信事不显，乃有见疑患。\n周公佐成王，金縢功不刊。\n推心辅王室，二叔反流言。\n待罪居东国，泣涕常流连。\n皇灵大动变，震雷风且寒。\n拔树偃秋稼，天威不可干。\n素服开金縢，感悟求其端。\n公旦事既显，成王乃哀叹。\n吾欲竟此曲，此曲悲且长。\n今日乐相乐，别后莫相忘。','乐府,抒怀,悲愤',83,'2019-03-01 22:54:47'),(92,'谒金门·春雨足','唐代','韦庄','春雨足，染就一溪新绿。\n柳外飞来双羽玉，弄晴相对浴。\n楼外翠帘高轴，倚遍阑干几曲。\n云淡水平烟树簇，寸心千里目。','写景,春天',1,'2019-03-01 22:54:48'),(93,'咏蕙诗','魏晋','繁钦','蕙草生山北，托身失所依。\n植根阴崖侧，夙夜惧危颓。\n寒泉浸我根，凄风常徘徊。\n三光照八极，独不蒙余晖。\n葩叶永雕瘁，凝露不暇晞。\n百卉皆含荣，己独失时姿。\n比我英芳发，鶗鴂鸣已衰。','咏物,赞美,品格',3,'2019-03-01 22:54:48'),(94,'登邯郸洪波台置酒观发兵','唐代','李白','我把两赤羽，来游燕赵间。\n天狼正可射，感激无时闲。\n观兵洪波台，倚剑望玉关。\n请缨不系越，且向燕然山。\n风引龙虎旗，歌钟昔追攀。\n击筑落高月，投壶破愁颜。\n遥知百战胜，定扫鬼方还。','边塞,军旅',55,'2019-03-01 22:54:48'),(95,'阳春曲·闺怨','元代','徐再思','妾身悔作商人妇，妾命当逢薄幸夫。\n别时只说到东吴，三载余，却得广州书。','闺怨',18,'2019-03-01 22:54:48'),(96,'小重山·昨夜寒蛩不住鸣','宋代','岳飞','昨夜寒蛩不住鸣。\n惊回千里梦，已三更。\n起来独自绕阶行。\n人悄悄，帘外月胧明。\n白首为功名。\n旧山松竹老，阻归程。\n欲将心事付瑶琴。\n知音少，弦断有谁听。','宋词三百首,宋词精选,豪放,壮志难酬',1048,'2019-03-01 22:54:48'),(97,'菩萨蛮·朔风吹散三更雪','清代','纳兰性德','朔风吹散三更雪，倩魂犹恋桃花月。\n梦好莫催醒，由他好处行。\n无端听画角，枕畔红冰薄。\n塞马一声嘶，残星拂大旗。','记梦,思念',104,'2019-03-01 22:54:48'),(98,'示三子','宋代','陈师道','时三子已归自外家\n去远即相忘，归近不可忍。\n儿女已在眼，眉目略不省。\n喜极不得语，泪尽方一哂。\n了知不是梦，忽忽心未稳。','古诗三百首,思亲',69,'2019-03-01 22:54:48'),(99,'天香·蜡梅','宋代','吴文英','蟫叶黏霜，蝇苞缀冻，生香远带风峭。\n岭上寒多，溪头月冷，北枝瘦、南枝小。\n玉奴有姊，先占立、墙阴春早。\n初试宫黄澹薄，偷分寿阳纤巧。\n银烛泪深未晓。\n酒钟悭、贮愁多少。\n记得短亭归马，暮衙蜂闹。\n豆蔻钗梁恨袅。\n但怅望、天涯岁华老。\n远信难封，吴云雁杳。','咏物,写花,离别,惜别',8,'2019-03-01 22:54:48'),(100,'南山田中行','唐代','李贺','秋野明，秋风白，塘水漻漻虫啧啧。\n云根苔藓山上石，冷红泣露娇啼色。\n荒畦九月稻叉牙，蛰萤低飞陇径斜。\n石脉水流泉滴沙，鬼灯如漆点松花。','秋天,写景',31,'2019-03-01 22:54:48'),(101,'点绛唇·闲倚胡床','宋代','苏轼','闲倚胡床，庾公楼外峰千朵。\n与谁同坐。\n明月清风我。\n 别乘一来，有唱应须和。\n还知么。\n自从添个。\n风月平分破。','叙事,山水,抒情',38,'2019-03-01 22:54:48'),(102,'淮阳感怀','隋代','李密','金风荡初节，玉露凋晚林。\n此夕穷涂士，郁陶伤寸心。\n野平葭苇合，村荒藜藿深。\n眺听良多感，徙倚独沾襟。\n沾襟何所为，怅然怀古意。\n秦俗犹未平，汉道将何冀。\n樊哙市井徒，萧何刀笔吏。\n一朝时运会，千古传名谥。\n寄言世上雄，虚生真可愧。','秋天,写景,抒怀,惆怅',13,'2019-03-01 22:54:48'),(103,'送湖南李正字归','唐代','韩愈','长沙入楚深，洞庭值秋晚。\n人随鸿雁少，江共蒹葭远。\n历历余所经，悠悠子当返。\n孤游怀耿介，旅宿梦婉娩。\n风土稍殊音，鱼虾日异饭。\n亲交俱在此，谁与同息偃。','送别,友人,抒情',1,'2019-03-01 22:54:48'),(104,'宿建德江','唐代','孟浩然','移舟泊烟渚，日暮客愁新。\n野旷天低树，江清月近人。','唐诗三百首,写景,月亮,写水,思乡,小学生必背古诗80首',2868,'2019-03-01 22:54:48'),(105,'灭胡曲','唐代','岑参','都护新灭胡，士马气亦粗。\n萧条虏尘净，突兀天山孤。','边塞,生活,豪迈',8,'2019-03-01 22:54:49'),(106,'惠崇春江晚景二首 / 惠崇春江晓景二首','宋代','苏轼','竹外桃花三两枝，春江水暖鸭先知。\n蒌蒿满地芦芽短，正是河豚欲上时。\n\n两两归鸿欲破群，依依还似北归人。\n遥知朔漠多风雪，更待江南半月春。','古诗三百首,题画,想象,写景,春天,写水,早教,小学生必背古诗70首,小学生必背古诗80首',2915,'2019-03-01 22:54:49'),(107,'赠道者','唐代','刘沧','窗中忽有鹤飞声，方士因知道欲成。\n来取图书安枕里，便驱鸡犬向山行。\n花开深洞仙门小，路过悬桥羽节轻。\n送客自伤身易老，不知何处待先生。','赠诗,女子,写狗',37,'2019-03-01 22:54:49'),(108,'人月圆·雪中游虎丘','元代','张可久','梅花浑似真真面，留我倚阑杆。\n雪晴天气，松腰玉瘦，泉眼冰寒。\n兴亡遗恨，一丘黄土，千古青山。\n老僧同醉，残碑休打，宝剑羞看。','冬天,写雪,写景,伤怀,壮志难酬',34,'2019-03-01 22:54:49'),(109,'孤雁 / 后飞雁','唐代','杜甫','孤雁不饮啄，飞鸣声念群。\n谁怜一片影，相失万重云？望尽似犹见，哀多如更闻。\n野鸦无意绪，鸣噪自纷纷。','咏物,写鸟,孤独,哀伤',342,'2019-03-01 22:54:49'),(110,'浣溪沙·红桥怀古和王阮亭韵','清代','纳兰性德','无恙年年汴水流。\n一声水调短亭秋。\n旧时明月照扬州。\n曾是长堤牵锦缆，绿杨清瘦至今愁。\n玉钩斜路近迷楼。','咏史,历史,怀古,感慨',13,'2019-03-01 22:54:49'),(111,'点绛唇·送李琴泉','宋代','吴大有','江上旗亭，送君还是逢君处。\n酒阑呼渡。\n云压沙鸥暮。\n 漠漠萧萧，香冻梨花雨。\n添愁绪。\n断肠柔橹。\n相逐寒潮去。','送别,写景',10,'2019-03-01 22:54:49'),(112,'闻雁','唐代','韦应物','故园渺何处，归思方悠哉。\n淮南秋雨夜，高斋闻雁来。','思乡,秋天,大雁',254,'2019-03-01 22:54:49'),(113,'游子吟','唐代','孟郊','慈母手中线，游子身上衣。\n临行密密缝，意恐迟迟归。\n谁言寸草心，报得三春晖。','唐诗三百首,乐府,赞颂,母亲,母爱,早教,小学生必背古诗70首,小学生必背古诗80首',8879,'2019-03-01 22:54:49'),(114,'山坡羊·未央怀古','元代','张养浩','三杰当日，俱曾此地，殷勤纳谏论兴废。\n见遗基，怎不伤悲！山河犹带英雄气，试上最高处闲坐地。\n东，也在图画里；西，也在图画里。','怀古,抒情',74,'2019-03-01 22:54:49'),(115,'采桑子·恨君不似江楼月','宋代','吕本中','恨君不似江楼月，南北东西，南北东西，只有相随无别离。\n恨君却似江楼月，暂满还亏，暂满还亏，待得团圆是几时？','宋词三百首,宋词精选,民歌,婉约,月亮,感叹,离别,离情',334,'2019-03-01 22:54:49'),(116,'九歌·云中君','先秦','屈原','浴兰汤兮沐芳，华采衣兮若英。\n灵连蜷兮既留，烂昭昭兮未央。\n蹇将憺兮寿宫，与日月兮齐光。\n龙驾兮帝服，聊翱游兮周章。\n灵皇皇兮既降，猋远举兮云中。\n览冀州兮有余，横四海兮焉穷。\n思夫君兮太息，极劳心兮忡忡。','楚辞,神话,祭祀',13,'2019-03-01 22:54:49'),(117,'云鬟烟鬓与谁期','宋代','王安石','云鬟烟鬓与谁期，一去天边更不归。\n还似九疑山下女，千秋长望舜裳衣。','夫妻,爱情,抒情,政治,抱负',8,'2019-03-01 22:54:49'),(118,'左掖梨花','唐代','丘为','冷艳全欺雪，余香乍入衣。\n春风且莫定，吹向玉阶飞。','梨花,君主,爱国',53,'2019-03-01 22:54:49'),(119,'白云歌送刘十六归山','唐代','李白','楚山秦山皆白云，白云处处长随君。\n长随君，君入楚山里，云亦随君渡湘水。\n湘水上，女萝衣，白云堪卧君早归。','送别,抒情,写云',47,'2019-03-01 22:54:49'),(120,'昭君怨·梅花','宋代','郑域','道是花来春未。\n道是雪来香异。\n竹外一枝斜。\n野人家。\n冷落竹篱茅舍。\n富贵玉堂琼榭。\n两地不同栽。\n一般开。','咏物,梅花,抒怀,怀才不遇',58,'2019-03-01 22:54:50'),(121,'长相思·秋风清','明代','李攀龙','秋风清，秋月明。\n叶叶梧桐槛外声。\n难教归梦成。\n砌蛩鸣，树鸟惊。\n塞雁行行天际横。\n偏伤旅客情。','乡情,写景',1,'2019-03-01 22:54:50'),(122,'薛氏瓜庐','宋代','赵师秀','不作封侯念，悠然远世纷。\n惟应种瓜事，犹被读书分。\n野水多于地，春山半是云。\n吾生嫌已老，学圃未如君。','写人,赞美,向往,隐居',2,'2019-03-01 22:54:50'),(123,'点绛唇·蹴罢秋千','宋代','李清照','蹴罢秋千，起来慵整纤纤手。\n露浓花瘦，薄汗轻衣透。\n见客入来，袜刬金钗溜。\n和羞走，倚门回首，却把青梅嗅。','宋词三百首,宋词精选,少女',1126,'2019-03-01 22:54:50'),(124,'虞美人·无聊','清代','陈维崧','无聊笑捻花枝说，处处鹃啼血。\n好花须映好楼台，休傍秦关蜀栈战场开。\n倚楼极目深愁绪，更对东风语。\n好风休簸战旗红，早送鲥鱼如雪过江东。','婉约,写景,抒怀',25,'2019-03-01 22:54:50'),(125,'清平乐·平原放马','宋代','张炎','辔摇衔铁。\n蹴踏平原雪。\n勇趁军声曾汗血。\n闲过升平时节。\n 茸茸春草天涯。\n涓涓野水晴沙。\n多少骅骝老去，至今犹困盐车。','婉约,写马,感慨',24,'2019-03-01 22:54:50'),(126,'山亭柳·赠歌者','宋代','晏殊','家住西秦。\n赌博艺随身。\n花柳上、斗尖新。\n偶学念奴声调，有时高遏行云。\n蜀锦缠头无数，不负辛勤。\n 数年来往咸京道，残杯冷炙谩消魂。\n衷肠事、托何人。\n若有知音见采，不辞遍唱阳春。\n一曲当筵落泪，重掩罗巾。','宋词三百首,失意,感慨',77,'2019-03-01 22:54:50'),(127,'春残','五代','翁宏','又是春残也，如何出翠帏？落花人独立，微雨燕双飞。\n寓目魂将断，经年梦亦非。\n那堪向愁夕，萧飒暮蝉辉。','女子,怀人',120,'2019-03-01 22:54:50'),(128,'燕山亭·北行见杏花','宋代','赵佶','裁剪冰绡，轻叠数重，淡著胭脂匀注。\n新样靓妆，艳溢香融，羞杀蕊珠宫女。\n易得凋零，更多少、无情风雨。\n愁苦。\n问院落凄凉，几番春暮。\n凭寄离恨重重，者双燕，何曾会人言语。\n天遥地远，万水千山，知他故宫何处。\n怎不思量，除梦里、有时曾去。\n无据，和梦也新来不做。','婉约,写花,寓人,身世,感伤',85,'2019-03-01 22:54:50'),(129,'四块玉·警世','元代','曾瑞','狗探汤，鱼着网，急走沿身痛着伤。\n柳腰花貌邪魔旺。\n柳弄娇，花艳妆，君莫赏。','劝诫,警世',5,'2019-03-01 22:54:50'),(130,'人月圆·茫茫大块洪炉里','元代','刘因','茫茫大块洪炉里，何物不寒灰。\n古今多少，荒烟废垒，老树遗台。\n太行如砺，黄河如带，等是尘埃。\n不须更叹，花开花落，春去春来。','登高,写景,抒怀,感慨,人生',8,'2019-03-01 22:54:50'),(131,'眼儿媚·平沙芳草渡头村','宋代','洪咨夔','平沙芳草渡头村。\n绿遍去年痕。\n游丝下上，流莺来往，无限销魂。\n 绮窗深静人归晚，金鸭水沈温。\n海棠影下，子规声里，立尽黄昏。','婉约,少妇,相思',16,'2019-03-01 22:54:50'),(132,'声声慢·秋声','宋代','蒋捷','黄花深巷，红叶低窗，凄凉一片秋声。\n豆雨声来，中间夹带风声。\n疏疏二十五点，丽谯门、不锁更声。\n故人远，问谁摇玉佩，檐底铃声？彩角声吹月堕，渐连营马动，四起笳声。\n闪烁邻灯，灯前尚有砧声。\n知他诉愁到晓，碎哝哝、多少蛩声！诉未了，把一半、分与雁声。','秋天,声音,抒怀',135,'2019-03-01 22:54:50'),(133,'邯郸少年行','唐代','高适','邯郸城南游侠子，自矜生长邯郸里。\n千场纵博家仍富，几度报仇身不死。\n宅中歌笑日纷纷，门外车马常如云，未知肝胆向谁是，今人却忆平原君。\n君不见即今交态薄，黄金用尽还疏索。\n以兹感叹辞旧游，更于时事无所求。\n且与少年饮美酒，往来射猎西山头。','游侠,生活',21,'2019-03-01 22:54:50'),(134,'秋兴八首·其一','唐代','杜甫','玉露凋伤枫树林，巫山巫峡气萧森。\n江间波浪兼天涌，塞上风云接地阴。\n丛菊两开他日泪，孤舟一系故园心。\n寒衣处处催刀尺，白帝城高急暮砧。','秋天,写景,思念',522,'2019-03-01 22:54:51'),(135,'别匡山','唐代','李白','晓峰如画参差碧，藤影风摇拂槛垂。\n野径来多将犬伴，人间归晚带樵随。\n看云客倚啼猿树，洗钵僧临失鹤池。\n莫怪无心恋清境，已将书剑许明时。','写景,生活,抒怀,抱负,写狗',21,'2019-03-01 22:54:51'),(136,'杂诗十二首·其二','魏晋','陶渊明','白日沦西阿，素月出东岭。\n遥遥万里晖，荡荡空中景。\n风来入房户，夜中枕席冷。\n气变悟时易，不眠知夕永。\n欲言无予和，挥杯劝孤影。\n日月掷人去，有志不获骋。\n念此怀悲凄，终晓不能静。','感慨,时间,壮志未酬',23,'2019-03-01 22:54:51'),(137,'谪居粮绝请学于农将田南山咏言寄怀','明代','王守仁','谪居履在陈，从者有温见。\n山荒聊可田，钱镈还易办。\n夷俗多火耕，仿习亦颇便。\n及兹春未深，数亩犹足佃。\n岂徒实口腹，且以理荒宴。\n遗穗及乌雀，贫寡发余羡。\n出耒在明晨，山寒易霜霰。','学习,农事,抒情',12,'2019-03-01 22:54:51'),(138,'滁州西涧','唐代','韦应物','独怜幽草涧边生，上有黄鹂深树鸣。\n 春潮带雨晚来急，野渡无人舟自横。','唐诗三百首,初中古诗,写景,山水,抒情,小学生必背古诗80首',3652,'2019-03-01 22:54:51'),(139,'八月十五夜月二首','唐代','杜甫','满月飞明镜，归心折大刀。\n转蓬行地远，攀桂仰天高。\n水路疑霜雪，林栖见羽毛。\n此时瞻白兔，直欲数秋毫。\n\n稍下巫山峡，犹衔白帝城。\n气沈全浦暗，轮仄半楼明。\n刁斗皆催晓，蟾蜍且自倾。\n张弓倚残魄，不独汉家营。','中秋节,月亮,思乡',106,'2019-03-01 22:54:51'),(140,'鹧鸪天·林断山明竹隐墙','宋代','苏轼','林断山明竹隐墙。\n乱蝉衰草小池塘。\n翻空白鸟时时见，照水红蕖细细香。\n村舍外，古城旁。\n杖藜徐步转斜阳。\n殷勤昨夜三更雨，又得浮生一日凉。','写景,写人,隐居,生活,抒怀',320,'2019-03-01 22:54:51'),(141,'送杜十四之江南 / 送杜晃进士之东吴','唐代','孟浩然','荆吴相接水为乡，君去春江正淼茫。\n(淼茫 同：渺)日暮征帆何处泊，天涯一望断人肠。','离别',162,'2019-03-01 22:54:51'),(142,'寄黄几复','宋代','黄庭坚','我居北海君南海，寄雁传书谢不能。\n桃李春风一杯酒，江湖夜雨十年灯。\n持家但有四立壁，治病不蕲三折肱。\n想见读书头已白，隔溪猿哭瘴溪藤。\n(想见 一作：想得)','思念,友情',540,'2019-03-01 22:54:51'),(143,'人有亡斧者','先秦','吕不韦 撰','人有亡斧者，意其邻人之子。\n视其行步，窃斧也；视其颜色，窃斧也；听其言语，窃斧也；动作态度，无为而不窃斧者也。\n俄而掘其沟而得其斧，他日，复见其邻之子，其行动、颜色、动作皆无似窃斧者也。','小学文言文,寓理,故事',512,'2019-03-01 22:54:51'),(144,'大德歌·夏','元代','关汉卿','俏冤家，在天涯，偏那里绿杨堪系马。\n困坐南窗下，数对清风想念他。\n蛾眉淡了教谁画？瘦岩岩羞戴石榴花。','夏天,妇女,相思,怨恨',52,'2019-03-01 22:54:51'),(145,'江神子·恨别','宋代','苏轼','天涯流落思无穷！既相逢，却匆匆。\n携手佳人，和泪折残红。\n为问东风余几许？春纵在，与谁同！隋堤三月水溶溶。\n背归鸿，去吴中。\n回首彭城，清泗与淮通。\n欲寄相思千点泪，流不到，楚江东。\n(欲寄 一作：寄我)','春天,离别,写景,抒情',184,'2019-03-01 22:54:51'),(146,'念奴娇·留别辛稼轩','宋代','刘过','知音者少，算乾坤许大，著身何处。\n直待功成方肯退，何日可寻归路。\n多景楼前，垂虹亭下，一枕眠秋雨。\n虚名相误，十年枉费辛苦。\n不是奏赋明光，上书北阙，无惊人之语。\n我自匆忙天未许，赢得衣裾尘土。\n白璧追欢，黄金买笑，付与君为主。\n莼鲈江上，浩然明日归去。','抒情,壮志未酬,感慨',11,'2019-03-01 22:54:51'),(147,'水仙子·赋李仁仲懒慢斋','元代','乔吉','闹排场经过乐回闲，勤政堂辞别撒会懒，急喉咙倒换学些慢。\n掇梯儿休上竿，梦魂中识破邯郸。\n昨日强如今日，这番险似那番。\n君不见倦鸟知还！','政治,抒情,失望,愤懑',1,'2019-03-01 22:54:52'),(148,'酒泉太守席上醉后作','唐代','岑参','酒泉太守能剑舞，高堂置酒夜击鼓。\n胡笳一曲断人肠，座上相看泪如雨。\n\n琵琶长笛曲相和，羌儿胡雏齐唱歌。\n浑炙犁牛烹野驼，交河美酒归叵罗。\n三更醉后军中寝，无奈秦山归梦何。','宴会,思乡',20,'2019-03-01 22:54:52'),(149,'霜叶飞·重九','宋代','吴文英','断烟离绪。\n关心事，斜阳红隐霜树。\n半壶秋水荐黄花，香噀西风雨。\n纵玉勒、轻飞迅羽，凄凉谁吊荒台古？记醉踏南屏， 彩扇咽寒蝉，倦梦不知蛮素。\n聊对旧节传杯，尘笺蠹管，断阕经岁慵赋。\n小蟾斜影转东篱，夜冷残蛩语。\n早白发、缘愁万缕。\n惊飙从卷乌纱去。\n谩细将、茱萸看，但约明年，翠微高处。','宋词精选,悼念',42,'2019-03-01 22:54:52'),(150,'洗然弟竹亭','唐代','孟浩然','吾与二三子，平生结交深。\n俱怀鸿鹄志，昔有鶺鴒心。\n逸气假毫翰，清风在竹林。\n达是酒中趣，琴上偶然音。','写竹,写人,言志',16,'2019-03-01 22:54:52'),(151,'满庭芳·碧水惊秋','宋代','秦观','碧水惊秋，黄云凝暮，败叶零乱空阶。\n洞房人静，斜月照徘徊。\n又是重阳近也，几处处，砧杵声催。\n西窗下，风摇翠竹，疑是故人来。\n 伤怀。\n增怅望，新欢易失，往事难猜。\n问篱边黄菊，知为谁开。\n谩道愁须殢酒，酒未醒、愁已先回。\n凭阑久，金波渐转，白露点苍苔。','婉约,秋天,伤怀',77,'2019-03-01 22:54:52'),(152,'商颂·烈祖','先秦','佚名','嗟嗟烈祖！有秩斯祜。\n申锡无疆，及尔斯所。\n既载清酤，赉我思成。\n亦有和羹，既戒既平。\n鬷假无言，时靡有争。\n绥我眉寿，黄耇无疆。\n约軧错衡，八鸾鸧鸧。\n以假以享，我受命溥将。\n自天降康，丰年穰穰。\n来假来飨，降福无疆。\n顾予烝尝，汤孙之将。','诗经,祭祀,颂歌',73,'2019-03-01 22:54:52'),(153,'螽斯','先秦','佚名','螽斯羽，诜诜兮。\n宜尔子孙，振振兮。\n螽斯羽，薨薨兮。\n宜尔子孙，绳绳兮。\n螽斯羽，揖揖兮。\n宜尔子孙，蛰蛰兮。','诗经,写鸟,祝福',503,'2019-03-01 22:54:52'),(154,'浣溪沙·香靥凝羞一笑开','宋代','秦观','香靥凝羞一笑开，柳腰如醉暖相挨，日长春困下楼台。\n照水有情聊整鬓，倚栏无绪更兜鞋，眼边牵系懒归来。','写人,女子,约会',21,'2019-03-01 22:54:52'),(155,'御带花·青春何处风光好','宋代','欧阳修','青春何处风光好？帝里偏爱元夕。\n万重缯彩，构一屏峰岭，半空金碧。\n宝檠银钰，耀绛幕、龙虎腾掷。\n沙堤远，雕轮绣毂，争走五王宅。\n雍容熙熙昼，会乐府神姬，海洞仙客。\n拽香摇翠，称执手行歌，锦街天陌。\n月淡寒轻，渐向晓、漏声寂寂。\n当年少，狂心未已，不醉怎归得！','节日,元宵节,抒情,豪迈',4,'2019-03-01 22:54:52'),(156,'读长恨辞','宋代','李觏','蜀道如天夜雨淫，乱铃声里倍沾襟。\n当时更有军中死，自是君王不动心。','札记,咏史,讽刺',42,'2019-03-01 22:54:52'),(157,'台城','唐代','韦庄','江雨霏霏江草齐，六朝如梦鸟空啼。\n 无情最是台城柳，依旧烟笼十里堤。','咏史怀古,写景,抒情',394,'2019-03-01 22:54:52'),(158,'怨诗行','未知','佚名','天德悠且长，人命一何促。\n百年未几时，奄若风吹烛。\n嘉宾难再遇，人命不可赎。\n齐度游四方，各系太山录。\n人间乐未央，忽然归东岳。\n当须荡中情，游心恣所欲。','乐府,感伤,人生',13,'2019-03-01 22:54:53'),(159,'国风·陈风·东门之池','先秦','佚名','东门之池，可以沤麻。\n彼美淑姬，可与晤歌。\n东门之池，可以沤纻。\n彼美淑姬，可与晤语。\n东门之池，可以沤菅。\n彼美淑姬，可与晤言。','诗经,劳动',43,'2019-03-01 22:54:53'),(160,'赋得鸡','唐代','李商隐','稻粱犹足活诸雏，妒敌专场好自娱。\n可要五更惊晓梦，不辞风雪为阳乌。','讽刺',7,'2019-03-01 22:54:53'),(161,'伯牙绝弦','未知','佚名','伯牙善鼓琴，钟子期善听。\n伯牙鼓琴，志在高山，钟子期曰：“善哉，峨峨兮若泰山！”志在流水，钟子期曰：“善哉，洋洋兮若江河！”伯牙所念，钟子期必得之。\n子期死，伯牙谓世再无知音，乃破琴绝弦，终身不复鼓。','小学文言文,音乐,知音,故事',1009,'2019-03-01 22:54:53'),(162,'杜司勋','唐代','李商隐','高楼风雨感斯文，短翼差池不及群。\n刻意伤春复伤别，人间惟有杜司勋。','写人,感慨,抒怀',10,'2019-03-01 22:54:53'),(163,'贺新郎·老去相如倦','宋代','刘过','老去相如倦。\n向文君、说似而今，怎生消遣？衣袂京尘曾染处，空有香红尚软。\n料彼此、魂消肠断。\n一枕新凉眠客舍，听梧桐疏雨秋风颤。\n灯晕冷，记初见。\n楼低不放珠帘卷。\n晚妆残，翠蛾狼藉，泪痕凝脸。\n人道愁来须殢酒，无奈愁深酒浅。\n但托意焦琴纨扇。\n莫鼓琵琶江上曲，怕荻花枫叶俱凄怨。\n云万叠，寸心远。','艳遇',34,'2019-03-01 22:54:53'),(164,'白帝城最高楼','唐代','杜甫','城尖径昃旌旆愁，独立缥缈之飞楼。\n峡坼云霾龙虎卧，江清日抱鼋鼍游。\n扶桑西枝对断石，弱水东影随长流。\n杖藜叹世者谁子，泣血迸空回白头。','写景,感叹,惆怅',32,'2019-03-01 22:54:53'),(165,'蝶恋花·两岸月桥花半吐','宋代','真德秀','两岸月桥花半吐。\n红透肌香，暗把游人误。\n尽道武陵溪上路。\n不知迷入江南去。\n 先自冰霜真态度。\n何事枝头，点点胭脂污。\n莫是东君嫌淡素。\n问花花又娇无语。','咏物,梅花',21,'2019-03-01 22:54:53'),(166,'送李侍御赴安西','唐代','高适','行子对飞蓬，金鞭指铁骢。\n功名万里外，心事一杯中。\n虏障燕支北，秦城太白东。\n离魂莫惆怅，看取宝刀雄！','送别,惜别,壮志',35,'2019-03-01 22:54:53'),(167,'高冠谷口招郑鄠','唐代','岑参','谷口来相访，空斋不见君。\n涧花然暮雨，潭树暖春云。\n门径稀人迹，檐峰下鹿群。\n衣裳与枕席，山霭碧氛氲。','写景,访友,抒情,隐居',8,'2019-03-01 22:54:53'),(168,'减字木兰花·春夜闻隔墙歌吹声','清代','项鸿祚','阑珊心绪，醉倚绿琴相伴住。\n一枕新愁，残夜花香月满楼。\n繁笙脆管，吹得锦屏春梦远。\n只有垂杨，不放秋千影过墙。','婉约,孤独,忧郁',16,'2019-03-01 22:54:53'),(169,'送董判官','唐代','高适','逢君说行迈，倚剑别交亲。\n幕府为才子，将军作主人。\n近关多雨雪，出塞有风尘。\n长策须当用，男儿莫顾身。','赠别,友人,勉励',12,'2019-03-01 22:54:53'),(170,'和袭美木兰后池三咏·白莲','唐代','陆龟蒙','素蘤多蒙别艳欺，此花端合在瑶池。\n无情有恨何人觉？月晓风清欲堕时。','咏物,莲花',32,'2019-03-01 22:54:53'),(171,'浣溪沙·百亩中庭半是苔','宋代','王安石','百亩中庭半是苔。\n门前白道水萦回。\n爱闲能有几人来。\n小院回廊春寂寂，山桃溪杏两三栽。\n为谁零落为谁开。','写景,抒情',76,'2019-03-01 22:54:53'),(172,'与南陵常赞府游五松山','唐代','李白','安石泛溟渤，独啸长风还。\n逸韵动海上，高情出人间。\n灵异可并迹，澹然与世闲。\n我来五松下，置酒穷跻攀。\n征古绝遗老，因名五松山。\n五松何清幽，胜境美沃洲。\n萧飒鸣洞壑，终年风雨秋。\n响入百泉去，听如三峡流。\n剪竹扫天花，且从傲吏游。\n龙堂若可憩，吾欲归精修。','游玩,写景',7,'2019-03-01 22:54:54'),(173,'望黄鹤楼','唐代','李白','东望黄鹤山，雄雄半空出。\n四面生白云，中峰倚红日。\n岩峦行穹跨，峰嶂亦冥密。\n颇闻列仙人，于此学飞术。\n一朝向蓬海，千载空石室。\n金灶生烟埃，玉潭秘清谧。\n地古遗草木，庭寒老芝术。\n蹇予羡攀跻，因欲保闲逸。\n观奇遍诸岳，兹岭不可匹。\n结心寄青松，永悟客情毕。','景点,抒情,生活',21,'2019-03-01 22:54:54'),(174,'送邢桂州','唐代','王维','铙吹喧京口，风波下洞庭。\n赭圻将赤岸，击汰复扬舲。\n日落江湖白，潮来天地青。\n明珠归合浦，应逐使臣星。','送别,友情',48,'2019-03-01 22:54:54'),(175,'狡童','先秦','佚名','彼狡童兮，不与我言兮。\n维子之故，使我不能餐兮。\n彼狡童兮，不与我食兮。\n维子之故，使我不能息兮。','诗经,女子',87,'2019-03-01 22:54:54'),(176,'绮罗香·红叶','宋代','张炎','万里飞霜，千林落木，寒艳不招春妒。\n枫冷吴江，独客又吟愁句。\n正船舣、流水孤村，似花绕、斜阳归路。\n甚荒沟、一片凄凉，载情不去载愁去。\n长安谁问倦旅？羞见衰颜借酒，飘零如许。\n谩倚新妆，不入洛阳花谱。\n为回风、起舞尊前，尽化作、断霞千缕。\n记阴阴、绿遍江南，夜窗听暗雨。','咏物,抒情,爱国',5,'2019-03-01 22:54:54'),(177,'黄鹤楼 / 登黄鹤楼','唐代','崔颢','昔人已乘黄鹤去，此地空余黄鹤楼。\n黄鹤一去不复返，白云千载空悠悠。\n晴川历历汉阳树，芳草萋萋鹦鹉洲。\n日暮乡关何处是？烟波江上使人愁。','唐诗三百首,初中古诗,写景,怀古,思乡,早教',5284,'2019-03-01 22:54:54'),(178,'天净沙·鲁卿庵中','元代','张可久','青苔古木萧萧，苍云秋水迢迢。\n红叶山斋小小，有谁曾到？探梅人过溪桥。','赞美,友人,向往',6,'2019-03-01 22:54:54'),(179,'木兰花慢·莺啼啼不尽','宋代','戴复古','莺啼啼不尽，任燕语、语难通。\n这一点闲愁，十年不断，恼乱春风。\n重来故人不见，但依然、杨柳小楼东。\n记得同题粉壁，而今壁破无踪。\n兰皋新涨绿溶溶。\n流恨落花红。\n念着破春衫，当时送别，灯下裁缝。\n相思谩然自苦，算云烟、过眼总成空。\n落日楚天无际，凭栏目送飞鸿。','宋词精选,悼亡',40,'2019-03-01 22:54:54'),(180,'南歌子·天上星河转','宋代','李清照','天上星河转，人间帘幕垂。\n凉生枕簟泪痕滋。\n起解罗衣聊问、夜何其。\n翠贴莲蓬小，金销藕叶稀。\n旧时天气旧时衣。\n只有情怀不似、旧家时。','宋词三百首,宋词精选,抒情',233,'2019-03-01 22:54:54'),(181,'鹑之奔奔','先秦','佚名','鹑之奔奔，鹊之彊彊。\n人之无良，我以为兄！鹊之彊彊，鹑之奔奔。\n人之无良，我以为君！','诗经,讽刺',86,'2019-03-01 22:54:54'),(182,'赠女冠畅师','宋代','秦观','瞳人剪水腰如束，一幅乌纱裹寒玉。\n飘然自有姑射姿，回看粉黛皆尘俗。\n雾阁云窗人莫窥，门前车马任东西。\n礼罢晓坛春日静，落红满地乳鸦啼。','写人,女子',8,'2019-03-01 22:54:54'),(183,'杨柳枝二首','唐代','白居易','一树春风千万枝，嫩于金色软于丝。\n永丰西角荒园里，尽日无人属阿谁。\n\n一树衰残委泥土，双枝荣耀植天庭。\n定知玄象今春后，柳宿光中添两星。','乐府,写柳',8,'2019-03-01 22:54:54'),(184,'泗州东城晚望','宋代','秦观','渺渺孤城白水环，舳舻人语夕霏间。\n林梢一抹青如画，应是淮流转处山。','山水,写景,抒情',9,'2019-03-01 22:54:55'),(185,'沁园春·代悼亡','清代','纳兰性德','梦冷蘅芜，却望姗姗，是耶非耶？怅兰膏渍粉，尚留犀合；金泥蹙绣，空掩蝉纱。\n影弱难持，缘深暂隔，只当离愁滞海涯。\n归来也，趁星前月底，魂在梨花。\n鸾胶纵续琵琶。\n问可及、当年萼绿华。\n但无端摧折，恶经风浪；不如零落，判委尘沙。\n最忆相看，娇讹道字，手剪银灯自泼茶。\n令已矣，便帐中重见，那似伊家。','悼亡,怀人',22,'2019-03-01 22:54:55'),(186,'添字丑奴儿·窗前谁种芭蕉树','宋代','李清照','窗前谁种芭蕉树，阴满中庭。\n阴满中庭。\n叶叶心心，舒卷有馀清。\n(馀清 一作：情)伤心枕上三更雨，点滴霖霪。\n点滴霖霪。\n愁损北人，不惯起来听。','咏物,爱国,思乡',132,'2019-03-01 22:54:55'),(187,'舟中望月','南北朝','朱超','大江阔千里，孤舟无四邻。\n唯余故楼月，远近必随人。\n入风先绕晕，排雾急移轮。\n若教长似扇，堪拂艳歌尘。','月亮,怀人,离别,愁绪',3,'2019-03-01 22:54:55'),(188,'午日处州禁竞渡','明代','汤显祖','独写菖蒲竹叶杯，蓬城芳草踏初回。\n情知不向瓯江死，舟楫何劳吊屈来。','端午节,议论,忧国忧民',37,'2019-03-01 22:54:55'),(189,'山人劝酒','唐代','李白','苍苍云松，落落绮皓。\n春风尔来为阿谁，蝴蝶忽然满芳草。\n秀眉霜雪颜桃花，骨青髓绿长美好。\n称是秦时避世人，劝酒相欢不知老。\n各守麋鹿志，耻随龙虎争。\n欻起佐太子，汉王乃复惊。\n顾谓戚夫人，彼翁羽翼成。\n归来商山下，泛若云无情。\n举觞酹巢由，洗耳何独清。\n浩歌望嵩岳，意气还相倾。','乐府,仕途,感伤',23,'2019-03-01 22:54:55'),(190,'过三闾庙','唐代','戴叔伦','沅湘流不尽，屈子怨何深。\n日暮秋风起，萧萧枫树林。','咏史怀古,题诗',63,'2019-03-01 22:54:55'),(191,'同族弟金城尉叔卿烛照山水壁画歌','唐代','李白','高堂粉壁图蓬瀛，烛前一见沧洲清。\n洪波汹涌山峥嵘，皎若丹丘隔海望赤城。\n光中乍喜岚气灭，谓逢山阴晴后雪。\n回溪碧流寂无喧，又如秦人月下窥花源。\n了然不觉清心魂，只将叠嶂鸣秋猿。\n与君对此欢未歇，放歌行吟达明发。\n却顾海客扬云帆，便欲因之向溟渤。','想象,自由,豪迈',3,'2019-03-01 22:54:55'),(192,'浣溪沙·湖上朱桥响画轮','宋代','欧阳修','湖上朱桥响画轮，溶溶春水浸春云，碧琉璃滑净无尘。\n当路游丝萦醉客，隔花啼鸟唤行人，日斜归去奈何春。','西湖,春光,眷恋',28,'2019-03-01 22:54:55'),(193,'四时田园杂兴·其二','宋代','范成大','梅子金黄杏子肥，麦花雪白菜花稀。\n日长篱落无人过，惟有蜻蜓蛱蝶飞。\n(惟 通 唯)','田园,写景,劳作,小学生必背古诗70首,小学生必背古诗80首',2353,'2019-03-01 22:54:55'),(194,'鹧鸪天·杨柳东塘细水流','清代','蒋春霖','杨柳东塘细水流，红窗睡起唤晴鸠。\n屏间山压眉心翠，镜里波生鬓角秋。\n临玉管，试琼瓯，醒时题恨醉时休。\n明朝花落归鸿尽，细雨春寒闭小楼。','婉约,写景,惜春',36,'2019-03-01 22:54:55'),(195,'木兰花慢·别西湖两诗僧','宋代','卢祖皋','嫩寒催客棹，载酒去、载诗归。\n正红叶漫山，清泉漱石，多少心期。\n三生溪桥话别，怅薜萝、犹惹翠云衣。\n不似今番醉梦，帝城几度斜晖。\n鸿飞。\n烟水氵弥氵弥。\n回首处，只君知。\n念吴江鹭忆，孤山鹤怨，依旧东西。\n高峰梦醒云起，是瘦吟、窗底忆君时。\n何日还寻后约，为余先寄梅枝。','相思',5,'2019-03-01 22:54:55'),(196,'采桑子·平生为爱西湖好','宋代','欧阳修','平生为爱西湖好，来拥朱轮。\n富贵浮云，俯仰流年二十春。\n归来恰似辽东鹤，城郭人民，触目皆新，谁识当年旧主人？','追忆,感慨,怅惘',23,'2019-03-01 22:54:55'),(197,'临江仙·滚滚长江东逝水','明代','杨慎','《廿一史弹词》第三段说秦汉开场词\n滚滚长江东逝水，浪花淘尽英雄。\n是非成败转头空。\n青山依旧在，几度夕阳红。\n白发渔樵江渚上，惯看秋月春风。\n一壶浊酒喜相逢。\n古今多少事，都付笑谈中。','咏史,抒怀,人生,哲理',3435,'2019-03-01 22:54:56'),(198,'江亭','唐代','杜甫','坦腹江亭暖，长吟野望时。\n水流心不竞，云在意俱迟。\n寂寂春将晚，欣欣物自私。\n江东犹苦战，回首一颦眉。','暮春,爱国',42,'2019-03-01 22:54:56'),(199,'御街行·霜风渐紧寒侵被','宋代','佚名','霜风渐紧寒侵被。\n听孤雁、声嘹唳。\n一声声送一声悲，云淡碧天如水。\n披衣告语：雁儿略住，听我些儿事。\n塔儿南畔城儿里，第三个、桥儿外，濒河西岸小红楼，门外梧桐雕砌。\n请教且与，低声飞过，那里有、人人无寐。','婉约,写鸟,抒情,思乡,恋情',14,'2019-03-01 22:54:56'),(200,'雪后晚晴四山皆青惟东山全白赋最爱东山晴后雪二绝句','宋代','杨万里','只知逐胜忽忘寒，小立春风夕照间。\n最爱东山晴后雪，软红光里涌银山。\n\n群山雪不到新晴，多作泥融少作冰。\n最爱东山晴后雪，却愁宜看不宜登。','写雪,写景,喜爱',120,'2019-03-01 22:54:56'),(201,'采蘩','先秦','佚名','于以采蘩？于沼于沚。\n于以用之？公侯之事。\n于以采蘩？于涧之中。\n于以用之？公侯之宫。\n被之僮僮，夙夜在公。\n被之祁祁，薄言还归。','诗经,写人,女子',217,'2019-03-01 22:54:56'),(202,'白帝城最高楼','唐代','杜甫','城尖径昃旌旆愁，独立缥缈之飞楼。\n峡坼云霾龙虎卧，江清日抱鼋鼍游。\n扶桑西枝对断石，弱水东影随长流。\n杖藜叹世者谁子，泣血迸空回白头。','写景,感叹,惆怅',32,'2019-03-01 22:54:56'),(203,'吴楚歌','魏晋','傅玄','燕人美兮赵女佳，其室则迩兮限层崖。\n云为车兮风为马，玉在山兮兰在野。\n云无期兮风有止，思多端兮谁能理？','思慕,女子',27,'2019-03-01 22:54:56'),(204,'村晚','宋代','雷震','草满池塘水满陂，山衔落日浸寒漪。\n牧童归去横牛背，短笛无腔信口吹。','农村,写景,儿童',540,'2019-03-01 22:54:56'),(205,'读山海经·其十','魏晋','陶渊明','精卫衔微木，将以填沧海。\n刑天舞干戚，猛志固常在。\n同物既无虑，化去不复悔。\n徒设在昔心，良辰讵可待。\n(辰 一作：晨)','古诗三百首,赞美,写人,神话',447,'2019-03-01 22:54:56'),(206,'殿前欢·畅幽哉','元代','贯云石','畅幽哉，春风无处不楼台。\n一时怀抱俱无奈，总对天开。\n就渊明归去来，怕鹤怨山禽怪，问甚功名在？酸斋是我，我是酸斋。','歌颂,田园,生活,感慨,怀才不遇',10,'2019-03-01 22:54:56'),(207,'阮郎归·有怀北游','宋代','张炎','钿车骄马锦相连，香尘逐管弦。\n瞥然飞过水秋千。\n清明寒食天。\n花贴贴，柳悬悬。\n莺房几醉眠。\n醉中不信有啼鹃。\n江南二十年。','生活,回忆',13,'2019-03-01 22:54:56'),(208,'观猎','唐代','王维','风劲角弓鸣，将军猎渭城。\n草枯鹰眼疾，雪尽马蹄轻。\n忽过新丰市，还归细柳营。\n回看射雕处，千里暮云平。','冬天,狩猎,军旅',1119,'2019-03-01 22:54:56'),(209,'折桂令·荆溪即事','元代','乔吉','问荆溪溪上人家，为甚人家，不种梅花？老树支门，荒蒲绕岸，苦竹圈笆。\n寺无僧狐狸样瓦，官无事乌鼠当衙。\n白水黄沙，倚遍栏干，数尽啼鸦。','同情,农民,揭露,社会,愤懑',6,'2019-03-01 22:54:56'),(210,'征人怨 / 征怨','唐代','柳中庸','岁岁金河复玉关，朝朝马策与刀环。\n 三春白雪归青冢，万里黄河绕黑山。','唐诗三百首,黄河,边塞,乡情,怨情',356,'2019-03-01 22:54:56'),(211,'诉衷情·莺语','唐代','温庭筠','莺语，花舞，春昼午，雨霏微。\n金带枕，宫锦，凤凰帷。\n 柳弱燕交飞，依依。\n辽阳音信稀，梦中归。','思妇,怀人,愿望',3,'2019-03-01 22:54:57'),(212,'送宇文六','唐代','常建','花映垂杨汉水清，微风林里一枝轻。\n即今江北还如此，愁杀江南离别情。','春天,写景,送别,愁苦',23,'2019-03-01 22:54:57'),(213,'踏莎行·二社良辰','宋代','陈尧佐','二社良辰，千秋庭院。\n翩翩又见新来燕。\n凤凰巢稳许为邻，潇湘烟暝来何晚。\n乱入红楼，低飞绿岸。\n画梁时拂歌尘散。\n为谁归去为谁来，主人恩重珠帘卷。','写鸟,感恩',10,'2019-03-01 22:54:57'),(214,'秋风辞','两汉','刘彻','秋风起兮白云飞，草木黄落兮雁南归。\n兰有秀兮菊有芳，怀佳人兮不能忘。\n泛楼船兮济汾河，横中流兮扬素波。\n箫鼓鸣兮发棹歌，欢乐极兮哀情多。\n少壮几时兮奈老何！','古诗三百首,秋天,写风,乐府,人生,岁月',928,'2019-03-01 22:54:57'),(215,'南乡子·云带雨','五代','李珣','云带雨，浪迎风，钓翁回棹碧湾中。\n春酒香熟鲈鱼美，谁同醉？缆却扁舟篷底睡。','渔人,生活',43,'2019-03-01 22:54:57'),(216,'南邻','唐代','杜甫','锦里先生乌角巾，园收芋栗未全贫。\n惯看宾客儿童喜，得食阶除鸟雀驯。\n秋水才深四五尺，野航恰受两三人。\n白沙翠竹江村暮，相对柴门月色新。','月夜,送别,生活',30,'2019-03-01 22:54:57'),(217,'减字木兰花·楼台向晓','宋代','欧阳修','楼台向晓。\n淡月低云天气好。\n翠幕风微。\n宛转梁州入破时。\n香生舞袂。\n楚女腰肢天与细。\n汗粉重匀。\n酒后轻寒不著人。','写人,赞美,女子',1,'2019-03-01 22:54:57'),(218,'官仓鼠','唐代','曹邺','官仓老鼠大如斗，见人开仓亦不走。\n 健儿无粮百姓饥，谁遣朝朝入君口。','讽刺,官吏,早教',241,'2019-03-01 22:54:57'),(220,'踏莎行·雪中看梅花','元代','王旭','两种风流，一家制作。\n雪花全似梅花萼。\n细看不是雪无香，天风吹得香零落。\n虽是一般，惟高一着。\n雪花不似梅花薄。\n梅花散彩向空山，雪花随意穿帘幕。','写雪,梅花,咏物',63,'2019-03-01 22:54:57'),(221,'柳含烟·隋堤柳','五代','毛文锡','隋堤柳，汴河旁，夹岸绿阴千里。\n龙舟凤舸木兰香，锦帆张。\n因梦江南春景好，一路流苏羽葆。\n笙歌未尽起横流，锁春愁。','柳树,讽刺',9,'2019-03-01 22:54:57'),(222,'踏莎行·郴州旅舍','宋代','秦观','雾失楼台，月迷津渡。\n桃源望断无寻处。\n可堪孤馆闭春寒，杜鹃声里斜阳暮。\n 驿寄梅花，鱼传尺素。\n砌成此恨无重数。\n郴江幸自绕郴山，为谁流下潇湘去。','宋词三百首,高中古诗,婉约,孤独,思念,羁旅',766,'2019-03-01 22:54:57'),(223,'捣练子令·深院静','五代','李煜','深院静，小庭空，断续寒砧断续风。\n无奈夜长人不寐，数声和月到帘栊。','宋词三百首,婉约,孤独,离情',269,'2019-03-01 22:54:57'),(224,'赠田叟','唐代','李商隐','荷筱衰翁似有情，相逢携手绕村行。\n烧畬晓映远山色，伐树暝传深谷声。\n鸥鸟忘机翻浃洽，交亲得路昧平生。\n抚躬道地诚感激，在野无贤心自惊。','写景,自然,讽刺,政治',2,'2019-03-01 22:54:57'),(225,'咏风','唐代','王勃','肃肃凉风生，加我林壑清。\n驱烟寻涧户，卷雾出山楹。\n去来固无迹，动息如有情。\n日落山水静，为君起松声。\n(版本一)\n肃肃凉景生，加我林壑清。\n驱烟寻涧户，卷雾出山楹。\n去来固无迹，动息如有情。\n日落山水静，为君起松声。\n(版本二)','咏物,言志,赞美,写风',152,'2019-03-01 22:54:57'),(226,'朝三暮四','先秦','佚名','宋有狙公者，爱狙，养之成群，能解狙之意；狙亦得公之心。\n损其家口，充狙之欲。\n俄而匮焉，将限其食，恐众狙之不训于己也。\n先诳之曰：“与若芧，朝三而暮四，足乎？”众狙皆起怒。\n俄而曰：“与若芧，朝四而暮三，足乎？”众狙皆伏而喜。','小学文言文,寓言,故事,哲理',335,'2019-03-01 22:54:57'),(227,'溱洧','先秦','佚名','溱与洧，方涣涣兮。\n士与女，方秉蕑兮。\n女曰观乎？士曰既且，且往观乎？洧之外，洵訏且乐。\n维士与女，伊其相谑，赠之以勺药。\n溱与洧，浏其清矣。\n士与女，殷其盈矣。\n女曰观乎？士曰既且，且往观乎？洧之外，洵訏且乐。\n维士与女，伊其将谑，赠之以勺药。','诗经,民谣,爱情',83,'2019-03-01 22:54:57'),(228,'再游玄都观','唐代','刘禹锡','百亩庭中半是苔，桃花净尽菜花开。\n种桃道士归何处，前度刘郎今又来。','游历,看花,仕途',270,'2019-03-01 22:54:57'),(229,'江城子·江景','宋代','苏轼','湖上与张先同赋，时闻弹筝\n凤凰山下雨初晴，水风清，晚霞明。\n一朵芙蕖，开过尚盈盈。\n何处飞来双白鹭，如有意，慕娉婷。\n忽闻江上弄哀筝，苦含情，遣谁听！烟敛云收，依约是湘灵。\n欲待曲终寻问取，人不见，数峰青。','婉约,山水,音乐',107,'2019-03-01 22:54:57'),(230,'如梦令·满院落花春寂','宋代','杨冠卿','满院落花春寂。\n风絮一帘斜日。\n翠钿晓寒轻，独倚秋千无力。\n无力。\n无力。\n蹙破远山愁碧。','婉约,写景,春愁',76,'2019-03-01 22:54:57'),(231,'过燕支寄杜位','唐代','岑参','燕支山西酒泉道，北风吹沙卷白草。\n长安遥在日光边，忆君不见令人老。','边塞,写景,思念,友人',13,'2019-03-01 22:54:57'),(232,'送杨山人归嵩阳','唐代','高适','不到嵩阳动十年，旧时心事已徒然。\n一二故人不复见，三十六峰犹眼前。\n夷门二月柳条色，流莺数声泪沾臆。\n凿井耕田不我招，知君以此忘帝力。\n山人好去嵩阳路，惟余眷眷长相忆。','写景,赞美,友人,感慨,岁月',1,'2019-03-01 22:54:57'),(233,'寒食还陆浑别业','唐代','宋之问','洛阳城里花如雪，陆浑山中今始发。\n旦别河桥杨柳风，夕卧伊川桃李月。\n伊川桃李正芳新，寒食山中酒复春。\n野老不知尧舜力，酣歌一曲太平人。','寒食节,写景,抒情',15,'2019-03-01 22:54:57'),(234,'沁园春·观潮','清代','吴伟业','八月奔涛，千尺崔嵬，砉然欲惊。\n似灵妃顾笑，神鱼进舞；冯夷击鼓，白马来迎。\n伍相鸱夷，钱王羽箭，怒气强于十万兵。\n峥嵘甚，讶雪山中断，银汉西倾。\n孤舟铁笛风清，待万里乘槎问客星。\n叹鲸鲵未翦，戈船满岸；蟾蜍正吐，歌管倾城。\n狎浪儿童，横江士女，笑指渔翁一叶轻。\n谁知道，是观潮枚叟，论水庄生。','豪放,写景,抒情',44,'2019-03-01 22:54:57'),(235,'杨柳枝·宜春苑外最长条','唐代','温庭筠','宜春苑外最长条，闲袅春风伴舞腰。\n正是玉人肠绝处，一渠春水赤栏桥。','春天,写景,抒情,伤怀',5,'2019-03-01 22:54:57'),(236,'浣溪沙·著酒行行满袂风','宋代','姜夔','予女媭家沔之山阳，左白湖，右云梦，春水方生，浸数千里，冬寒沙露，衰草入云。\n丙午之秋，予与安甥或荡舟采菱，或举火罝兔，或观鱼簺下；山行野吟，自适其适；凭虚怅望，因赋是阕。\n\n著酒行行满袂风。\n草枯霜鹘落晴空。\n销魂都在夕阳中。\n恨入四弦人欲老，梦寻千驿意难通。\n当时何似莫匆匆。','写景,怀人,相思',10,'2019-03-01 22:54:57'),(237,'临江仙·西湖春泛','宋代','赵溍','堤曲朱墙近远，山明碧瓦高低。\n好风二十四花期。\n骄骢穿柳去，文艗挟春飞。\n 箫鼓晴雷殷殷，笑歌香雾霏霏，间情不受酒禁持。\n断肠无立处，斜日欲归时。','纪游,春天,西湖,写景,抒怀',6,'2019-03-01 22:54:58'),(238,'生查子·烟雨晚晴天','五代','魏承班','烟雨晚晴天，零落花无语。\n难话此时心，梁燕双来去。\n琴韵对薰风，有恨和情抚。\n肠断断弦频，泪滴黄金缕。','婉约,女子,孤独',12,'2019-03-01 22:54:58'),(239,'人月圆·吴门怀古','元代','张可久','山藏白虎云藏寺，池上老梅枝。\n洞庭归兴，香柑红树，鲈鲙银丝。\n白家池馆，吴宫花草，可似当时。\n最怜人处，啼乌夜月，犹怨西施。','怀古,抒情,隐居',3,'2019-03-01 22:54:58'),(240,'同王征君湘中有怀','唐代','张谓','八月洞庭秋，潇湘水北流。\n还家万里梦，为客五更愁。\n不用开书帙，偏宜上酒楼。\n故人京洛满，何日复同游。','秋天,写景,抒情,思乡',24,'2019-03-01 22:54:58'),(241,'柏林寺南望','唐代','郎士元','溪上遥闻精舍钟，泊舟微径度深松。\n青山霁后云犹在，画出东南四五峰。\n(东南 一作：西南)','写山,寺庙',80,'2019-03-01 22:54:58'),(242,'蝶恋花·越女采莲秋水畔','宋代','欧阳修','越女采莲秋水畔。\n窄袖轻罗，暗露双金钏。\n照影摘花花似面。\n芳心只共丝争乱。\n鸂鶒滩头风浪晚。\n雾重烟轻，不见来时伴。\n隐隐歌声归棹远。\n离愁引著江南岸。','女子',103,'2019-03-01 22:54:58'),(243,'贺新郎·夏景','宋代','苏轼','乳燕飞华屋。\n悄无人、桐阴转午，晚凉新浴。\n手弄生绡白团扇，扇手一时似玉。\n渐困倚、孤眠清熟。\n帘外谁来推绣户，枉教人、梦断瑶台曲。\n又却是，风敲竹。\n 石榴半吐红巾蹙。\n待浮花、浪蕊都尽，伴君幽独。\n秾艳一枝细看取，芳心千重似束。\n又恐被、秋风惊绿。\n若待得君来向此，花前对酒不忍触。\n共粉泪，两簌簌。','宋词精选,婉约,夏天,写景,咏物,写花,女子,孤独',198,'2019-03-01 22:54:58'),(244,'花下醉','唐代','李商隐','寻芳不觉醉流霞，倚树沉眠日已斜。\n客散酒醒深夜后，更持红烛赏残花。','宴游',91,'2019-03-01 22:54:58'),(245,'怨歌行','南北朝','庾信','家住金陵县前，嫁得长安少年。\n回头望乡泪落，不知何处天边？胡尘几日应尽？汉月何时更圆？为君能歌此曲，不觉心随断弦！','抒情,思念,家乡',2,'2019-03-01 22:54:58'),(246,'早行','宋代','陈与义','露侵驼褐晓寒轻，星斗阑干分外明。\n寂寞小桥和梦过，稻田深处草虫鸣。','羁旅,抒情,孤寂,愁绪',8,'2019-03-01 22:54:58'),(247,'铜驼悲','唐代','李贺','落魄三月罢，寻花去东家。\n谁作送春曲，洛岸悲铜驼。\n桥南多马客，北山饶古人。\n客饮杯中酒，驼悲千万春。\n生世莫徒劳，风吹盘上烛。\n厌见桃株笑，铜驼夜来哭。','抒情,悲伤,愁苦',15,'2019-03-01 22:54:58'),(248,'菩萨蛮·西湖','宋代','苏轼','秋风湖上萧萧雨。\n使君欲去还留住。\n今日漫留君。\n明朝愁杀人。\n佳人千点泪。\n洒向长河水。\n不用敛双蛾。\n路人啼更多。','友人,离别,不舍',9,'2019-03-01 22:54:58'),(249,'欲与元八卜邻先有是赠','唐代','白居易','平生心迹最相亲，欲隐墙东不为身。\n明月好同三径夜，绿杨宜作两家春。\n每因暂出犹思伴，岂得安居不择邻。\n可独终身数相见，子孙长作隔墙人。','想象,写景,友人',22,'2019-03-01 22:54:58'),(250,'泛南湖至石帆诗','南北朝','谢惠连','轨息陆途初，枻鼓川路始。\n涟漪繁波漾，参差层峰峙。\n萧疏野趣生，逶迤白云起。\n登陟苦跋涉，䁹盼乐心耳。\n即玩玩有竭，在兴兴无已。','自然,写景,山水',2,'2019-03-01 22:54:58'),(251,'凛凛岁云暮','两汉','佚名','凛凛岁云暮，蝼蛄夕鸣悲。\n凉风率已厉，游子寒无衣。\n锦衾遗洛浦，同袍与我违。\n独宿累长夜，梦想见容辉。\n良人惟古欢，枉驾惠前绥。\n愿得常巧笑，携手同车归。\n既来不须臾，又不处重闱。\n亮无晨风翼，焉能凌风飞？眄睐以适意，引领遥相睎。\n徙倚怀感伤，垂涕沾双扉。','古诗三百首,古诗十九首,叙梦,抒情',93,'2019-03-01 22:54:58'),(252,'木兰花慢·拆桐花烂漫','宋代','柳永','拆桐花烂漫，乍疏雨、洗清明。\n正艳杏浇林，缃桃绣野，芳景如屏。\n倾城。\n尽寻胜去，骤雕鞍紺幰出郊坰。\n风暖繁弦脆管，万家竞奏新声。\n 盈盈。\n斗草踏青。\n人艳冶、递逢迎。\n向路傍往往，遗簪堕珥，珠翠纵横。\n欢情。\n对佳丽地，信金罍罄竭玉山倾。\n拚却明朝永日，画堂一枕春酲。','踏青,写景',46,'2019-03-01 22:54:58'),(253,'采桑子·花前失却游春侣','五代','冯延巳','花前失却游春侣，独自寻芳。\n满目悲凉。\n纵有笙歌亦断肠。\n林间戏蝶帘间燕，各自双双。\n忍更思量，绿树青苔半夕阳。','婉约,孤独,思念,春天',112,'2019-03-01 22:54:58'),(254,'得献吉江西书','明代','何景明','近得浔阳江上书，遥思李白更愁予。\n天边魑魅窥人过，日暮鼋鼍傍客居。\n鼓柁湘江应未得，买田阳羡定何如？他年淮水能相访，桐柏山中共结庐。','赠友,友谊,抒情',2,'2019-03-01 22:54:58'),(255,'小儿不畏虎','宋代','苏轼','有妇人昼日置小儿沙上而浣衣于水者。\n虎自山上驰来，妇人仓皇沉水避之。\n二小儿戏沙上自若。\n虎熟视久之，至以首抵触，庶几其一就惧；而儿痴，竟不知。\n虎亦寻卒去。\n噫，虎之食人，先被之以威，而不惧之人，威亦无所施欤！','初中文言文,哲理,寓言,故事',116,'2019-03-01 22:54:58'),(256,'少年游·参差烟树灞陵桥','宋代','柳永','参差烟树灞陵桥，风物尽前朝。\n衰杨古柳，几经攀折，憔悴楚宫腰。\n 夕阳闲淡秋光老，离思满蘅皋。\n一曲阳关，断肠声尽，独自凭兰桡。','羁旅,写景,离别,怀古伤今',116,'2019-03-01 22:54:58'),(257,'田舍','宋代','范成大','呼唤携锄至，安排筑圃忙。\n儿童眠落叶，鸟雀噪斜阳。\n烟火村声远，林菁野气香。\n乐哉今岁事，天末稻云黄。','农村,农民,生活',6,'2019-03-01 22:54:59'),(258,'昭君怨·送别','宋代','苏轼','谁作桓伊三弄，惊破绿窗幽梦？新月与愁烟，满江天。\n欲去又还不去，明日落花飞絮。\n飞絮送行舟，水东流。','典故,离别,写景',93,'2019-03-01 22:54:59'),(259,'望湘人·春思','宋代','贺铸','厌莺声到枕，花气动帘，醉魂愁梦相半。\n被惜余薰，带惊剩眼，几许伤春春晚。\n泪竹痕鲜，佩兰香老，湘天浓暖。\n记小江风月佳时，屡约非烟游伴。\n须信鸾弦易断。\n奈云和再鼓，曲终人远。\n认罗袜无踪，旧处弄波清浅。\n青翰棹舣，白蘋洲畔，尽目临皋飞观。\n不解寄、一字相思，幸有归来双燕。','怀人,悼亡,思念,春天',11,'2019-03-01 22:54:59'),(260,'清明日','唐代','温庭筠','清娥画扇中，春树郁金红。\n出犯繁花露，归穿弱柳风。\n 马骄偏避幰，鸡骇乍开笼。\n柘弹何人发，黄鹂隔故宫。','清明节,踏青',193,'2019-03-01 22:54:59'),(261,'浣溪沙·一向年光有限身','宋代','晏殊','一向年光有限身，等闲离别易销魂，酒筵歌席莫辞频。\n满目山河空念远，落花风雨更伤春，不如怜取眼前人。','宋词三百首,婉约,感叹,离别,优美,春天',568,'2019-03-01 22:54:59'),(262,'示长安君','宋代','王安石','少年离别意非轻，老去相逢亦怆情。\n草草杯盘共笑语，昏昏灯火话平生。\n自怜湖海三年隔，又作尘沙万里行。\n欲问后期何日是，寄书应见雁南征。','兄妹,重逢,离别,感伤',33,'2019-03-01 22:54:59'),(263,'满江红·清江风帆甚快作此与客剧饮歌之','宋代','范成大','千古东流，声卷地，云涛如屋。\n横浩渺、樯竿十丈，不胜帆腹。\n夜雨翻江春浦涨，船头鼓急风初熟。\n似当年、呼禹乱黄川，飞梭速。\n击楫誓、空警俗。\n休拊髀，都生肉。\n任炎天冰海，一杯相属。\n荻笋蒌芽新入馔，弦凤吹能翻曲。\n笑人间、何处似尊前，添银烛。','写水,抒怀,愤懑,愁苦',17,'2019-03-01 22:54:59'),(264,'生查子·侍女动妆奁','唐代','韩偓','侍女动妆奁，故故惊人睡。\n那知本未眠，背面偷垂泪。\n懒卸凤凰钗，羞入鸳鸯被。\n时复见残灯，和烟坠金穗。','女子,思夫',65,'2019-03-01 22:54:59'),(265,'蝶恋花·记得珠帘初卷处','近现代','康有为','记得珠帘初卷处，人倚阑干，被酒刚微醉。\n翠叶飘零秋自语，晓风吹堕横塘路。\n词客看花心意苦，坠粉零香，果是谁相误。\n三十六陂飞细雨，明朝颜色难如故。','感叹,命运,宽慰',39,'2019-03-01 22:54:59'),(266,'将进酒','唐代','李贺','琉璃钟，琥珀浓，小槽酒滴真珠红。\n烹龙炮凤玉脂泣，罗帏绣幕围香风。\n吹龙笛，击鼍鼓；皓齿歌，细腰舞。\n况是青春日将暮，桃花乱落如红雨。\n劝君终日酩酊醉，酒不到刘伶坟上土。','乐府,宴饮,讽喻,人生',551,'2019-03-01 22:54:59'),(267,'郡斋雨中与诸文士燕集','唐代','韦应物','兵卫森画戟，宴寝凝清香。\n海上风雨至，逍遥池阁凉。\n烦疴近消散，嘉宾复满堂。\n自惭居处崇，未睹斯民康。\n理会是非遣，性达形迹忘。\n鲜肥属时禁，蔬果幸见尝。\n俯饮一杯酒，仰聆金玉章。\n神欢体自轻，意欲凌风翔。\n吴中盛文史，群彦今汪洋。\n方知大藩地，岂曰财赋强。','唐诗三百首,宴会',43,'2019-03-01 22:54:59'),(268,'汉宫春·次韵稼轩蓬莱阁','宋代','姜夔','一顾倾吴。\n苎萝人不见，烟杳重湖。\n当时事如对弈，此亦天乎。\n大夫仙去，笑人间、千古须臾。\n有倦客、扁舟夜泛，犹疑水鸟相呼。\n秦山对楼自绿，怕越王故垒，时下樵苏。\n只今倚阑一笑，然则非欤。\n小丛解唱，倩松风、为我吹竽。\n更坐待、千岩月落，城头眇眇啼鸟。','写景,怀古,感慨',4,'2019-03-01 22:54:59'),(269,'一剪梅·袁州解印','宋代','刘克庄','陌上行人怪府公，还是诗穷，还是文穷？下车上马太匆匆，来是春风，去是秋风。\n阶衔免得带兵农，嬉到昏钟，睡到斋钟。\n不消提岳与知宫，唤作山翁，唤作溪翁。','仕途',25,'2019-03-01 22:54:59'),(270,'女冠子·四月十七','唐代','韦庄','四月十七，正是去年今日，别君时。\n忍泪佯低面，含羞半敛眉。\n不知魂已断，空有梦相随。\n除却天边月，没人知。','宋词三百首,爱情,离别,相思',187,'2019-03-01 22:54:59'),(271,'寒食','宋代','赵鼎','寂寂柴门村落里，也教插柳记年华。\n禁烟不到粤人国，上冢亦携庞老家。\n汉寝唐陵无麦饭，山溪野径有梨花。\n一樽径籍青苔卧，莫管城头奏暮笳。','寒食节,风俗,抒情',39,'2019-03-01 22:54:59'),(272,'玩月城西门廨中','南北朝','鲍照','始出西南楼，纤纤如玉钩。\n末映东北墀，娟娟似蛾眉。\n蛾眉蔽珠栊，玉钩隔琐窗。\n三五二八时，千里与君同。\n夜移衡汉落，徘徊帷户中。\n归华先委露，别叶早辞风。\n客游厌苦辛，仕子倦飘尘。\n休澣自公日，宴慰及私辰。\n蜀琴抽白雪，郢曲发阳春。\n肴干酒未阕，金壶启夕沦。\n回轩驻轻盖，留酌待情人。','赏月,叙事,抒怀',19,'2019-03-01 22:54:59'),(273,'唐崇徽公主手痕和韩内翰','宋代','欧阳修','故乡飞鸟尚啁啾，何况悲笳出塞愁。\n青冢埋魂知不返，翠崖遗迹为谁留。\n玉颜自古为身累，肉食何人与国谋。\n行路至今空叹息，岩花涧草自春秋。','抒情,谴责,同情',11,'2019-03-01 22:54:59'),(274,'蟾宫曲·梦中作','元代','郑光祖','半窗幽梦微茫，歌罢钱塘，赋罢高唐。\n风入罗帏，爽入疏棂，月照纱窗。\n缥缈见梨花淡妆，依稀闻兰麝余香。\n唤起思量，待不思量，怎不思量！','爱情,相思,惆怅',20,'2019-03-01 22:54:59'),(275,'端午日礼部宿斋有衣服彩结之贶以诗还答','唐代','权德舆','良辰当五日，偕老祝千年。\n彩缕同心丽，轻裾映体鲜。\n 寂寥斋画省，款曲擘香笺。\n更想传觞处，孙孩遍目前。','端午节',16,'2019-03-01 22:54:59'),(276,'巴陵夜别王八员外 / 三湘有怀','唐代','贾至','柳絮飞时别洛阳，梅花发后到三湘。\n 世情已逐浮云散，离恨空随江水长。','送别,悲愁',26,'2019-03-01 22:54:59'),(277,'长门怨二首','唐代','李白','天回北斗挂西楼，金屋无人萤火流。\n月光欲到长门殿，别作深宫一段愁。\n\n桂殿长愁不记春，黄金四屋起秋尘。\n夜悬明镜青天上，独照长门宫里人。','宫怨',40,'2019-03-01 22:54:59'),(278,'出塞作','唐代','王维','居延城外猎天骄，白草连天野火烧。\n暮云空碛时驱马，秋日平原好射雕。\n护羌校尉朝乘障，破虏将军夜渡辽。\n玉靶角弓珠勒马，汉家将赐霍嫖姚。','边塞,军旅',38,'2019-03-01 22:55:00'),(279,'满江红·点火樱桃','宋代','辛弃疾','点火樱桃，照一架、荼蘼如雪。\n春正好，见龙孙穿破，紫苔苍壁。\n乳燕引雏飞力弱，流莺唤友娇声怯。\n问春归、不肯带愁归，肠千结。\n层楼望，春山叠；家何在？烟波隔。\n把古今遗恨，向他谁说？蝴蝶不传千里梦，子规叫断三更月。\n听声声、枕上劝人归，归难得。','写景,伤春,爱国,思归',91,'2019-03-01 22:55:00'),(280,'自君之出矣','南北朝','刘裕','自君之出矣，金翠闇无精。\n思君如日月，回还昼夜生。','抒情,思念',2,'2019-03-01 22:55:00'),(281,'息夫人','唐代','王维','莫以今时宠，难忘旧日恩。\n看花满眼泪，不共楚王言。','咏史,讽喻',86,'2019-03-01 22:55:00'),(282,'白鼻騧','唐代','李白','银鞍白鼻騧，绿地障泥锦。\n细雨春风花落时，挥鞭直就胡姬饮。','写景,抒情,豪迈',11,'2019-03-01 22:55:00'),(283,'和答钱穆父咏猩猩毛笔','宋代','黄庭坚','爱酒醉魂在，能言机事疏。\n平生几两屐，身后五车书。\n物色看王会，勋劳在石渠。\n拔毛能济世，端为谢杨朱。','咏物',16,'2019-03-01 22:55:00'),(284,'汉江临泛 / 汉江临眺','唐代','王维','楚塞三湘接，荆门九派通。\n江流天地外，山色有无中。\n郡邑浮前浦，波澜动远空。\n襄阳好风日，留醉与山翁。','唐诗三百首,山水,抒情',412,'2019-03-01 22:55:00'),(285,'凉州馆中与诸判官夜集','唐代','岑参','弯弯月出挂城头，城头月出照凉州。\n凉州七里十万家，胡人半解弹琵琶。\n琵琶一曲肠堪断，风萧萧兮夜漫漫。\n河西幕中多故人，故人别来三五春。\n花门楼前见秋草，岂能贫贱相看老。\n一生大笑能几回，斗酒相逢须醉倒。','友情,豪迈,地点,月亮,宴会',228,'2019-03-01 22:55:00'),(286,'三姝媚·过都城旧居有感','宋代','吴文英','湖山经醉惯。\n渍春衫、啼痕酒痕无限。\n又客长安，叹断襟零袂，涴尘谁浣。\n紫曲门荒，沿败井、风摇青蔓。\n对语东邻，犹是曾巢，谢堂双燕。\n 春梦人间须断。\n但怪得、当年梦缘能短。\n绣屋秦筝，傍海棠偏爱，夜深开宴。\n舞歇歌沈，花未减、红颜先变。\n伫久河桥欲去，斜阳泪满。','宋词精选,悼亡,追忆,写景,怀念',8,'2019-03-01 22:55:00'),(287,'浣溪沙·倾国倾城恨有馀','唐代','薛昭蕴','倾国倾城恨有馀，几多红泪泣姑苏，倚风凝睇雪肌肤。\n吴主山河空落日，越王宫殿半平芜，藕花菱蔓满重湖。','写人,咏史,伤怀',24,'2019-03-01 22:55:00'),(288,'浣溪沙·握手河桥柳似金','唐代','薛昭蕴','握手河桥柳似金；蜂须轻惹百花心，蕙风兰思寄清琴。\n意满便同春水满，情深还似酒杯深，楚烟湘月两沈沈。','离别,饮酒',24,'2019-03-01 22:55:00'),(289,'书愤五首·其一','宋代','陆游','早岁那知世事艰，中原北望气如山。\n楼船夜雪瓜洲渡，铁马秋风大散关。\n塞上长城空自许，镜中衰鬓已先斑。\n出师一表真名世，千载谁堪伯仲间！','古诗三百首,爱国,忆旧',1271,'2019-03-01 22:55:00'),(290,'于中好·谁道阴山行路难','清代','纳兰性德','谁道阴山行路难。\n风毛雨血万人欢。\n松梢露点沾鹰绁，芦叶溪深没马鞍。\n依树歇，映林看。\n黄羊高宴簇金盘。\n萧萧一夕霜风紧，却拥貂裘怨早寒。','边塞,写景,抒情,豪迈',12,'2019-03-01 22:55:00'),(291,'秋夜','宋代','朱淑真','夜久无眠秋气清，烛花频剪欲三更。\n铺床凉满梧桐月，月在梧桐缺处明。','闺怨,秋天,夜晚,抒情,孤寂,惆怅',20,'2019-03-01 22:55:00'),(292,'沁园春·寒食郓州道中','宋代','谢枋得','十五年来，逢寒食节，皆在天涯。\n叹雨濡露润，还思宰柏，风柔日媚，羞看飞花。\n麦饭纸钱，只鸡斗酒，几误林间噪喜鸦。\n天笑道，此不由乎我，也不由他。\n 鼎中炼熟丹砂。\n把紫府清都作一家。\n想前人鹤驭，常游绛阙，浮生蝉蜕，岂恋黄沙。\n帝命守坟，王令修墓，男子正当如是邪。\n又何必，待过家上冢，书锦荣华。','寒食节,思乡,爱国',9,'2019-03-01 22:55:00'),(293,'九歌·云中君','先秦','屈原','浴兰汤兮沐芳，华采衣兮若英。\n灵连蜷兮既留，烂昭昭兮未央。\n蹇将憺兮寿宫，与日月兮齐光。\n龙驾兮帝服，聊翱游兮周章。\n灵皇皇兮既降，猋远举兮云中。\n览冀州兮有余，横四海兮焉穷。\n思夫君兮太息，极劳心兮忡忡。','楚辞,神话,祭祀',13,'2019-03-01 22:55:00'),(294,'金菊对芙蓉·上元','清代','纳兰性德','金鸭消香，银虬泻水，谁家夜笛飞声。\n正上林雪霁，鸳甃晶莹。\n鱼龙舞罢香车杳，剩尊前、袖掩吴绫。\n狂游似梦，而今空记，密约烧灯。\n追念往事难凭。\n叹火树星桥，回首飘零。\n但九逵烟月，依旧笼明。\n楚天一带惊烽火，问今宵、可照江城。\n小窗残酒，阑珊灯灺，别自关情。','节日,元宵节,抒怀,思念',7,'2019-03-01 22:55:00'),(295,'哭曼卿','宋代','苏舜钦','去年春雨开百花，与君相会欢无涯。\n高歌长吟插花醉，醉倒不去眠君家。\n今年恸哭来致奠，忍欲出送攀魂车。\n春辉照眼一如昨，花已破颣兰生芽。\n唯君颜色不复见，精魂飘忽随朝霞。\n归来悲痛不能食，壁上遗墨如栖鸦。\n呜呼死生遂相隔，使我双泪风中斜。','追忆,友谊,感慨',10,'2019-03-01 22:55:00'),(296,'绝句漫兴九首·其七','唐代','杜甫','糁径杨花铺白毡，点溪荷叶叠青钱。\n笋根雉子无人见，沙上凫雏傍母眠。','夏天,写景,田园,组诗',88,'2019-03-01 22:55:00'),(297,'周颂·思文','先秦','佚名','思文后稷，克配彼天。\n立我烝民，莫菲尔极。\n贻我来牟，帝命率育，无此疆尔界。\n陈常于时夏。','诗经,赞颂,祭祀,写人',45,'2019-03-01 22:55:00'),(298,'卜算子·泛西湖坐间寅斋同赋','宋代','高观国','屈指数春来，弹指惊春去。\n檐外蛛丝网落花，也要留春住。\n几日喜春晴，几夜愁春雨。\n十二雕窗六曲屏，题遍伤心句。','婉约,伤春,惜春',51,'2019-03-01 22:55:00'),(299,'浣溪沙·从石楼石壁往来邓尉山中','清代','郑文焯','一半黄梅杂雨晴，虚岚浮翠带湖明，闲云高鸟共身轻。\n山果打头休论价，野花盈手不知名，烟峦直是画中行。','婉约,写景,咏物,喜悦',26,'2019-03-01 22:55:00'),(300,'人月圆·重冈已隔红尘断','金朝','元好问','重冈已隔红尘断，村落更年丰。\n移居要就，窗中远岫，舍后长松。\n十年种木，一年种谷，都付儿童。\n老夫惟有，醒来明月，醉后清风。','生活,写景,抒情',85,'2019-03-01 22:55:01'),(301,'采蘩','先秦','佚名','于以采蘩？于沼于沚。\n于以用之？公侯之事。\n于以采蘩？于涧之中。\n于以用之？公侯之宫。\n被之僮僮，夙夜在公。\n被之祁祁，薄言还归。','诗经,写人,女子',217,'2019-03-01 22:55:01'),(302,'临江仙·和叶仲洽赋羊桃','宋代','辛弃疾','忆醉三山芳树下，几曾风韵忘怀。\n黄金颜色五花开，味如卢橘熟。\n贵似荔枝来。\n闻道商山余四老，橘中自酿秋醅。\n试呼名品细推排。\n重重香腑脏，偏殢圣贤杯。','咏物,植物',8,'2019-03-01 22:55:01'),(303,'山中','唐代','王勃','长江悲已滞，万里念将归。\n况属高风晚，山山黄叶飞。','山水,思乡',3006,'2019-03-01 22:55:01'),(304,'南乡子·春情','宋代','苏轼','晚景落琼杯，照眼云山翠作堆。\n认得岷峨春雪浪，初来，万顷蒲萄涨渌醅。\n春雨暗阳台，乱洒歌楼湿粉腮。\n一阵东风来卷地，吹回，落照江天一半开。','春天,写景,抒情,思乡',17,'2019-03-01 22:55:01'),(305,'鹊踏枝·谁道闲情抛掷久','五代','冯延巳','谁道闲情抛掷久？每到春来，惆怅还依旧。\n日日花前常病酒，敢辞镜里朱颜瘦。\n(敢辞 一作：不辞)河畔青芜堤上柳，为问新愁，何事年年有？独立小桥风满袖，平林新月人归后。','宋词三百首,婉约,春愁,感叹,人生',443,'2019-03-01 22:55:01'),(306,'减字木兰花·立春','宋代','苏轼','春牛春杖，无限春风来海上。\n便与春工，染得桃红似肉红。\n春幡春胜，一阵春风吹酒醒。\n不似天涯，卷起杨花似雪花。','春天,习俗,写景,抒怀',83,'2019-03-01 22:55:01'),(307,'南歌子·凤髻金泥带','宋代','欧阳修','凤髻金泥带，龙纹玉掌梳。\n走来窗下笑相扶，爱道画眉深浅入时无？弄笔偎人久，描花试手初。\n等闲妨了绣功夫，笑问鸳鸯两字怎生书？','新婚,生活',89,'2019-03-01 22:55:01'),(308,'虞美人·曲阑深处重相见','清代','纳兰性德','曲阑深处重相见，匀泪偎人颤。\n凄凉别后两应同，最是不胜清怨月明中。\n半生已分孤眠过，山枕檀痕涴。\n忆来何事最销魂，第一折枝花样画罗裙。','离别,相思',722,'2019-03-01 22:55:01'),(309,'劝学','唐代','颜真卿','三更灯火五更鸡，正是男儿读书时。\n黑发不知勤学早，白首方悔读书迟。','励志,惜时,读书,哲理,早教',2378,'2019-03-01 22:55:01'),(310,'长安寒食','唐代','邵谒','春日照九衢。\n春风媚罗绮。\n万骑出都门，拥在香尘里。\n莫辞吊枯骨，千载长如此。\n安知今日身，不是昔时鬼。\n但看平地游，亦见摧辀死。','寒食节,讽刺,郊游',13,'2019-03-01 22:55:01'),(311,'下武','先秦','佚名','下武维周，世有哲王。\n三后在天，王配于京。\n\n王配于京，世德作求。\n永言配命，成王之孚。\n\n成王之孚，下土之式。\n永言孝思，孝思维则。\n\n媚兹一人，应侯顺德。\n永言孝思，昭哉嗣服。\n\n昭兹来许，绳其祖武。\n於万斯年，受天之祜。\n\n受天之祜，四方来贺。\n於万斯年，不遐有佐。','诗经,赞颂,写人',29,'2019-03-01 22:55:01'),(312,'踏莎行·初春','清代','徐灿','芳草才芽，梨花未雨，春魂已作天涯絮。\n晶帘宛转为谁垂，金衣飞上樱桃树。\n故国茫茫，扁舟何许，夕阳一片江流去。\n碧云犹叠旧河山，月痕休到深深处。','春天,写景,抒情,感伤',26,'2019-03-01 22:55:01'),(313,'清平乐·池上纳凉','清代','项鸿祚','水天清话，院静人销夏。\n蜡炬风摇帘不下，竹影半墙如画。\n醉来扶上桃笙，熟罗扇子凉轻。\n一霎荷塘过雨，明朝便是秋声。','婉约,夏天,写景',33,'2019-03-01 22:55:01'),(314,'木兰花令·元宵似是欢游好','宋代','苏轼','元宵似是欢游好。\n何况公庭民讼少。\n万家游赏上春台，十里神仙迷海岛。\n平原不似高阳傲。\n促席雍容陪语笑。\n坐中有客最多情，不惜玉山拚醉倒。','元宵节,游玩,抒情',10,'2019-03-01 22:55:01'),(315,'桂殿秋·思往事','清代','朱彝尊','思往事，渡江干，青蛾低映越山看。\n共眠一舸听秋雨，小簟轻衾各自寒。','婉约,怀念,爱情,恋情',79,'2019-03-01 22:55:01'),(316,'南乡子·相见处','五代','李珣','相见处，晚晴天，刺桐花下越台前。\n暗里回眸深属意，遗双翠，骑象背人先过水。','写人,少女,爱情,恋情',27,'2019-03-01 22:55:01'),(317,'御街行·别东山','宋代','贺铸','松门石路秋风扫。\n似不许、飞尘到。\n双携纤手别烟萝，红粉清泉相照。\n几声歌管，正须陶写，翻作伤心调。\n 岩阴暝色归云悄。\n恨易失、千金笑。\n更逢何物可忘忧，为谢江南芳草。\n断桥孤驿，冷云黄叶，相见长安道。','怀人,惆怅,思念',4,'2019-03-01 22:55:01'),(318,'题宗之家初序潇湘图','宋代','吴激','江南春水碧于酒，客子往来船是家。\n忽见画图疑是梦，而今鞍马老风沙。','乡情,题画',1,'2019-03-01 22:55:01'),(319,'戊辰即事','宋代','刘克庄','诗人安得有青衫，今岁和戎百万缣。\n从此西湖休插柳，剩栽桑树养吴蚕。','国家,讽刺,政治',6,'2019-03-01 22:55:01'),(320,'蝶恋花·春景','宋代','苏轼','花褪残红青杏小。\n燕子飞时，绿水人家绕。\n枝上柳绵吹又少。\n天涯何处无芳草！墙里秋千墙外道。\n墙外行人，墙里佳人笑。\n笑渐不闻声渐悄。\n多情却被无情恼。','宋词三百首,婉约,写景,春天,伤春',4189,'2019-03-01 22:55:01'),(321,'人月圆·伤心莫问前朝事','元代','倪瓒','伤心莫问前朝事，重上越王台。\n鹧鸪啼处，东风草绿，残照花开。\n怅然孤啸，青山故国，乔木苍苔。\n当时明月，依依素影，何处飞来？','咏史怀古',36,'2019-03-01 22:55:01'),(322,'陇头吟','唐代','王维','长安少年游侠客，夜上戍楼看太白。\n陇头明月迥临关，陇上行人夜吹笛。\n关西老将不胜愁，驻马听之双泪流。\n身经大小百余战，麾下偏裨万户侯。\n苏武才为典属国，节旄空尽海西头。','边塞,感伤,悲愤',54,'2019-03-01 22:55:01'),(323,'台城','唐代','刘禹锡','台城六代竞豪华，结绮临春事最奢。\n万户千门成野草，只缘一曲后庭花。','怀古',56,'2019-03-01 22:55:01'),(324,'踏歌词四首·其三','唐代','刘禹锡','新词宛转递相传，振袖倾鬟风露前。\n月落乌啼云雨散，游童陌上拾花钿。','写人,赞美',1,'2019-03-01 22:55:01'),(325,'捉船行','清代','吴伟业','官差捉船为载兵，大船买脱中船行。\n中船芦港且潜避，小船无知唱歌去。\n郡符昨下吏如虎，快桨追风摇急橹。\n村人露肘捉头来，背似土牛耐鞭苦。\n苦辞船小要何用，争执汹汹路人拥。\n前头船见不敢行，晓事篙题敛钱送。\n船户家家坏十千，官司查点侯如年。\n发回仍索常行费，另派门摊云雇船。\n君不见，官舫嵬峨无用处，打彭插旗马头住。','揭露,社会,同情,人民',2,'2019-03-01 22:55:01'),(326,'送陈七赴西军','唐代','孟浩然','吾观非常者，碌碌在目前。\n君负鸿鹄志，蹉跎书剑年。\n 一闻边烽动，万里忽争先。\n余亦赴京国，何当献凯还。','离别,赞扬,友人,愤懑',43,'2019-03-01 22:55:01'),(327,'出颍口初见淮山是日至寿州','宋代','苏轼','我行日夜向江海，枫叶芦花秋兴长。\n长淮忽迷天远近，青山久与船低昂。\n寿州已见白石塔，短棹未转黄茅冈。\n波平风软望不到，故人久立烟苍茫。','写景,抒情,惆怅,感慨',9,'2019-03-01 22:55:01'),(328,'发白马','唐代','李白','将军发白马，旌节度黄河。\n箫鼓聒川岳，沧溟涌涛波。\n武安有振瓦，易水无寒歌。\n铁骑若雪山，饮流涸滹沱。\n扬兵猎月窟，转战略朝那。\n倚剑登燕然，边烽列嵯峨。\n萧条万里外，耕作五原多。\n一扫清大漠，包虎戢金戈。','乐府,写马,战争',36,'2019-03-01 22:55:01'),(329,'狱中题壁','近现代','谭嗣同','望门投止思张俭，忍死须臾待杜根。\n我自横刀向天笑，去留肝胆两昆仑。\n(版本一)\n望门投趾怜张俭，直谏陈书愧杜根。\n手掷欧刀仰天笑，留将公罪后人论。\n(版本二)','古诗三百首,爱国,悲壮,早教,猖狂',1251,'2019-03-01 22:55:01'),(330,'鹧鸪天·别情','宋代','聂胜琼','玉惨花愁出凤城，莲花楼下柳青青。\n尊前一唱阳关曲，别个人人第五程。\n寻好梦，梦难成。\n有谁知我此时情，枕前泪共阶前雨，隔个窗儿滴到明。','婉约,离别,相思',130,'2019-03-01 22:55:01'),(331,'唐多令·雨过水明霞','宋代','邓剡','雨过水明霞。\n潮回岸带沙。\n叶声寒、飞透窗纱。\n堪恨西风吹世换，更吹我、落天涯。\n 寂寞古豪华。\n乌衣日又斜。\n说兴亡、燕入谁家。\n惟有南来无数雁，和明月、宿芦花。','写景,孤独,感伤,爱国',39,'2019-03-01 22:55:01'),(332,'山坡羊·大江东去','元代','薛昂夫','大江东去，长安西去，为功名走遍天涯路。\n厌舟车，喜琴书，早星星鬓影瓜田暮。\n心待足时名便足。\n高，高处苦；低，低处苦。','抒怀,感慨,人生',9,'2019-03-01 22:55:02'),(333,'剑器近·夜来雨','宋代','袁去华','夜来雨。\n赖倩得、东风吹住。\n海棠正妖娆处。\n且留取。\n悄庭户。\n试细听、莺啼燕语。\n分明共人愁绪。\n怕春去。\n佳树。\n翠阴初转午。\n重帘卷，乍睡起、寂寞看风絮。\n偷弹清泪寄烟波，见江头故人，为言憔悴如许。\n彩笺无数。\n去却寒暄，到了浑无定据。\n断肠落日千山暮。','写雨,写景,相思,春天',17,'2019-03-01 22:55:02'),(334,'落梅风·心间事','元代','马致远','心间事，说与他。\n动不动早言两罢。\n罢字儿碜可可你道是耍，我心里怕那不怕？','爱情,抒怀',5,'2019-03-01 22:55:02'),(335,'青蝇','先秦','佚名','营营青蝇，止于樊。\n岂弟君子，无信谗言。\n\n营营青蝇，止于棘。\n谗人罔极，交乱四国。\n\n营营青蝇，止于榛。\n谗人罔极，构我二人。','诗经,讽刺',33,'2019-03-01 22:55:02'),(336,'误佳期·闺怨','清代','汪懋麟','寒气暗侵帘幕，孤负芳春小约。\n庭梅开遍不归来，直恁心情恶。\n独抱影儿眠，背看灯花落。\n待他重与画眉时，细数郎轻薄。','婉约,闺怨,孤独',14,'2019-03-01 22:55:02'),(337,'水调歌头·金山观月','宋代','张孝祥','江山自雄丽，风露与高寒。\n寄声月姊，借我玉鉴此中看。\n幽壑鱼龙悲啸，倒影星辰摇动，海气夜漫漫。\n涌起白银阙，危驻紫金山。\n 表独立，飞霞佩，切云冠。\n漱冰濯雪，眇视万里一毫端。\n回首三山何处，闻道群仙笑我，要我欲俱还。\n挥手从此去，翳凤更骖鸾。','豪放,登高,写景,长江,抒情',263,'2019-03-01 22:55:02'),(338,'古风·其十九','唐代','李白','西上莲花山，迢迢见明星。\n(西上 一作：西岳)素手把芙蓉，虚步蹑太清。\n霓裳曳广带，飘拂升天行。\n邀我登云台，高揖卫叔卿。\n恍恍与之去，驾鸿凌紫冥。\n俯视洛阳川，茫茫走胡兵。\n流血涂野草，豺狼尽冠缨。','游仙,同情,忧国,人民,生活',99,'2019-03-01 22:55:02'),(339,'自君之出矣','南北朝','刘裕','自君之出矣，金翠闇无精。\n思君如日月，回还昼夜生。','抒情,思念',2,'2019-03-01 22:55:02'),(340,'观博平王志安少府山水粉图','唐代','李白','粉壁为空天，丹青状江海。\n游云不知归，日见白鸥在。\n博平真人王志安，沈吟至此愿挂冠。\n松溪石磴带秋色，愁客思归坐晓寒。','写景,抒怀,思乡,豪迈',8,'2019-03-01 22:55:02'),(341,'蝶恋花·辛苦最怜天上月','清代','纳兰性德','辛苦最怜天上月，一昔如环，昔昔都成玦。\n若似月轮终皎洁，不辞冰雪为卿热。\n无那尘缘容易绝，燕子依然，软踏帘钩说。\n唱罢秋坟愁未歇，春丛认取双栖蝶。','婉约,悼亡,爱情',523,'2019-03-01 22:55:02'),(342,'听筝','唐代','李端','鸣筝金粟柱，素手玉房前。\n欲得周郎顾，时时误拂弦。','唐诗三百首,音乐',294,'2019-03-01 22:55:02'),(343,'迷神引·红板桥头秋光暮','宋代','柳永','红板桥头秋光暮。\n淡月映烟方煦。\n寒溪蘸碧，绕垂杨路。\n重分飞，携纤手、泪如雨。\n波急隋堤远，片帆举。\n倏忽年华改，向期阻。\n时觉春残，渐渐飘花絮。\n好夕良天长孤负。\n洞房闲掩，小屏空、无心觑。\n指归云，仙乡杳、在何处。\n遥夜香衾暖，算谁与。\n知他深深约，记得否。','秋天,离别,相思,怀人',11,'2019-03-01 22:55:02'),(344,'荷叶杯·一点露珠凝冷','唐代','温庭筠','一点露珠凝冷，波影。\n满池塘，绿茎红艳两相乱。\n肠断，水风凉。','荷塘,生活',72,'2019-03-01 22:55:02'),(345,'江城子·墨云拖雨过西楼','宋代','苏轼','墨云拖雨过西楼。\n水东流。\n晚烟收。\n柳外残阳，回照动帘钩。\n今夜巫山真个好，花未落，酒新篘。\n 美人微笑转星眸。\n月花羞。\n捧金瓯。\n歌扇萦风，吹散一春愁。\n试问江南诸伴侣，谁似我，醉扬州。','宋词精选,悼亡,追忆,写景,怀念',114,'2019-03-01 22:55:02'),(346,'三姝媚·过都城旧居有感','宋代','吴文英','湖山经醉惯。\n渍春衫、啼痕酒痕无限。\n又客长安，叹断襟零袂，涴尘谁浣。\n紫曲门荒，沿败井、风摇青蔓。\n对语东邻，犹是曾巢，谢堂双燕。\n 春梦人间须断。\n但怪得、当年梦缘能短。\n绣屋秦筝，傍海棠偏爱，夜深开宴。\n舞歇歌沈，花未减、红颜先变。\n伫久河桥欲去，斜阳泪满。','古诗三百首,爱国,悲歌,优美',8,'2019-03-01 22:55:02'),(347,'荆轲歌 / 渡易水歌','先秦','佚名','风萧萧兮易水寒，壮士一去兮不复还。\n(复还 一作：复反)探虎穴兮入蛟宫，仰天呼气兮成白虹。\n(此句疑为后人补作)','月夜,写景,怀人,友人',2042,'2019-03-01 22:55:02'),(348,'月夜江行寄崔员外宗之','唐代','李白','飘飘江风起，萧飒海树秋。\n登舻美清夜，挂席移轻舟。\n月随碧山转，水合青天流。\n杳如星河上，但觉云林幽。\n归路方浩浩，徂川去悠悠。\n徒悲蕙草歇，复听菱歌愁。\n岸曲迷后浦，沙明瞰前洲。\n怀君不可见，望远增离忧。','山水',13,'2019-03-01 22:55:02'),(349,'彭蠡湖中望庐山','唐代','孟浩然','太虚生月晕，舟子知天风。\n挂席候明发，渺漫平湖中。\n中流见匡阜，势压九江雄。\n黤黕凝黛色，峥嵘当曙空。\n香炉初上日，瀑水喷成虹。\n久欲追尚子，况兹怀远公。\n我来限于役，未暇息微躬。\n淮海途将半，星霜岁欲穷。\n寄言岩栖者，毕趣当来同。','赞美,写人,自然',29,'2019-03-01 22:55:02'),(350,'谷口书斋寄杨补阙','唐代','钱起','泉壑带茅茨，云霞生薜帷。\n竹怜新雨后，山爱夕阳时。\n闲鹭栖常早，秋花落更迟。\n家僮扫萝径，昨与故人期。','唐诗三百首,写景,邀请',28,'2019-03-01 22:55:02'),(351,'周颂·烈文','先秦','佚名','烈文辟公，锡兹祉福。\n惠我无疆，子孙保之。\n无封靡于尔邦，维王其崇之。\n念兹戎功，继序其皇之。\n无竞维人，四方其训之。\n不显维德，百辟其刑之。\n於乎，前王不忘！','诗经,赞颂',38,'2019-03-01 22:55:02'),(352,'过湖北山家','清代','施闰章','路回临石岸，树老出墙根。\n野水合诸涧，桃花成一村。\n呼鸡过篱栅，行酒尽儿孙。\n老矣吾将隐，前峰恰对门。','古诗三百首,山水,田园,写景',10,'2019-03-01 22:55:02'),(353,'游西池','魏晋','谢混','悟彼蟋蟀唱，信此劳者歌。\n有来岂不疾，良游常蹉跎。\n逍遥越城肆，愿言屡经过。\n回阡被陵阙，高台眺飞霞。\n惠风荡繁囿，白云屯曾阿。\n景昃鸣禽集，水木湛清华。\n褰裳顺兰沚，徙倚引芳柯。\n美人愆岁月，迟暮独如何？无为牵所思，南荣戒其多。','写景,山水',1,'2019-03-01 22:55:02'),(354,'华清宫三首·其三','唐代','崔橹','门横金锁悄无人，落日秋声渭水滨。\n红叶下山寒寂寂，湿云如梦雨如尘。','写景,感慨',4,'2019-03-01 22:55:02'),(355,'隰桑','先秦','佚名','隰桑有阿，其叶有难。\n既见君子，其乐如何。\n\n隰桑有阿，其叶有沃。\n既见君子，云何不乐。\n\n隰桑有阿，其叶有幽。\n既见君子，德音孔胶。\n\n心乎爱矣，遐不谓矣？中心藏之，何日忘之！','诗经,爱情',82,'2019-03-01 22:55:02'),(356,'荆州贼平临洞庭言怀作','唐代','李白','修蛇横洞庭，吞象临江岛。\n积骨成巴陵，遗言闻楚老。\n水穷三苗国，地窄三湘道。\n岁晏天峥嵘，时危人枯槁。\n思归阴丧乱，去国伤怀抱。\n郢路方丘墟，章华亦倾倒。\n风悲猿啸苦，木落鸿飞早。\n日隐西赤沙，月明东城草。\n关河望已绝，氛雾行当扫。\n长叫天可闻，吾将问苍昊。','战争,现实,愤懑,爱国',4,'2019-03-01 22:55:02'),(357,'浣溪沙·已落芙蓉并叶凋','近现代','王国维','已落芙蓉并叶凋。\n半枯萧艾过墙高。\n日斜孤馆易魂消。\n坐觉清秋归荡荡，眼看白日去昭昭。\n人间争度渐长宵。','秋天,伤怀,悲伤',13,'2019-03-01 22:55:02'),(358,'齐天乐·蝉','宋代','王沂孙','一襟余恨宫魂断，年年翠阴庭树。\n乍咽凉柯，还移暗叶，重把离愁深诉。\n西窗过雨。\n怪瑶佩流空，玉筝调柱。\n镜暗妆残，为谁娇鬓尚如许。\n铜仙铅泪似洗，叹携盘去远，难贮零露。\n病翼惊秋，枯形阅世，消得斜阳几度？馀音更苦。\n甚独抱清高，顿成凄楚？谩想熏风，柳丝千万缕。','婉约,咏物,寓人,抒情',44,'2019-03-01 22:55:02'),(359,'春远 / 春运','唐代','杜甫','肃肃花絮晚，菲菲红素轻。\n日长唯鸟雀，春远独柴荆。\n 数有关中乱，何曾剑外清。\n故乡归不得，地入亚夫营。','春天,思乡',169,'2019-03-01 22:55:02'),(360,'如鱼水·帝里疏散','宋代','柳永','帝里疏散，数载酒萦花系，九陌狂游。\n良景对珍筵恼，佳人自有风流。\n劝琼瓯。\n绛唇启、歌发清幽。\n被举措、艺足才高，在处别得艳姬留。\n浮名利，拟拚休。\n是非莫挂心头。\n富贵岂由人，时会高志须酬。\n莫闲愁。\n共绿蚁、红粉相尤。\n向绣幄，醉倚芳姿睡，算除此外何求。','生活,无奈,感慨',8,'2019-03-01 22:55:02'),(361,'永遇乐·次韵辛克清先生','宋代','姜夔','我与先生，夙期已久，人间无此。\n不学杨郎，南山种豆，十一征微利。\n云霄直上，诸公衮衮，乃作道边苦李。\n五千言、老来受用，肯教造物儿戏？东冈记得，同来胥宇，岁月几何难计。\n柳老悲桓，松高对阮，未办为邻地。\n长干白下，青楼朱阁，往往梦中槐蚁。\n却不如、洼尊放满，老夫未醉。','友谊,赞美,友人,品格',17,'2019-03-01 22:55:02'),(362,'听话丛台','唐代','李远','有客新从赵地回，自言曾上古丛台。\n云遮襄国天边去，树绕漳河地里来。\n弦管变成山鸟哢，绮罗留作野花开。\n金舆玉辇无行迹，风雨惟知长绿苔。','抒情,吊古伤今',1,'2019-03-01 22:55:02'),(363,'人月圆·宴北人张侍御家有感','宋代','吴激','南朝千古伤心事，犹唱后庭花。\n旧时王谢、堂前燕子，飞向谁家。\n恍然一梦，仙肌胜雪，宫髻堆鸦。\n江州司马，青衫泪湿，同是天涯。','婉约,怀古,感伤',44,'2019-03-01 22:55:02'),(364,'十二月过尧民歌·别情','元代','王实甫','自别后遥山隐隐，更那堪远水粼粼。\n见杨柳飞绵滚滚，对桃花醉脸醺醺。\n透内阁香风阵阵，掩重门暮雨纷纷。\n怕黄昏忽地又黄昏，不销魂怎地不销魂。\n新啼痕压旧啼痕，断肠人忆断肠人。\n今春香肌瘦几分？缕带宽三寸。','闺怨,写景,女子,思念',87,'2019-03-01 22:55:02'),(365,'渔家傲·临水纵横回晚鞚','宋代','苏轼','临水纵横回晚鞚。\n归来转觉情怀动。\n梅笛烟中闻几弄。\n秋阴重，西山雪淡云凝冻。\n美酒一杯谁与共。\n尊前舞雪狂歌送。\n腰跨金鱼旌旆拥。\n将何用，只堪妆点浮生梦。','送别,抒情,心情,愤懑',17,'2019-03-01 22:55:03'),(366,'赠渔父','唐代','杜牧','芦花深泽静垂纶，月夕烟朝几十春。\n 自说孤舟寒水畔，不曾逢著独醒人。','写景,赞美',8,'2019-03-01 22:55:03'),(367,'登鹿门山怀古','唐代','孟浩然','清晓因兴来，乘流越江岘。\n沙禽近方识，浦树遥莫辨。\n 渐至鹿门山，山明翠微浅。\n岩潭多屈曲，舟楫屡回转。\n 昔闻庞德公，采药遂不返。\n金涧饵芝朮，石床卧苔藓。\n 纷吾感耆旧，结揽事攀践。\n隐迹今尚存，高风邈已远。\n 白云何时去，丹桂空偃蹇。\n探讨意未穷，回艇夕阳晚。','登高,怀古',17,'2019-03-01 22:55:03'),(368,'采桑子·荷花开后西湖好','宋代','欧阳修','荷花开后西湖好，载酒来时。\n不用旌旗，前后红幢绿盖随。\n画船撑入花深处，香泛金卮。\n烟雨微微，一片笙歌醉里归。','写水,西湖,荷花,抒情',288,'2019-03-01 22:55:03'),(369,'临江仙·千里潇湘挼蓝浦','宋代','秦观','千里潇湘挼蓝浦，兰桡昔日曾经。\n月高风定露华清。\n微波澄不动，冷浸一天星。\n独倚危樯情悄悄，遥闻妃瑟泠泠。\n新声含尽古今情。\n曲终人不见，江上数峰青。','写景,月夜,写人,抒情,伤怀',33,'2019-03-01 22:55:03'),(370,'小长干曲','唐代','崔国辅','月暗送湖风，相寻路不通。\n菱歌唱不彻，知在此塘中。','江南,水乡,情歌,爱情',21,'2019-03-01 22:55:03'),(371,'行经华阴','唐代','崔颢','岧峣太华俯咸京，天外三峰削不成。\n武帝祠前云欲散，仙人掌上雨初晴。\n河山北枕秦关险，驿路西连汉畤平。\n借问路旁名利客，何如此处学长生？','写景,写山,抒情',1,'2019-03-01 22:55:03'),(372,'夜归鹿门山歌 / 夜归鹿门歌','唐代','孟浩然','山寺钟鸣昼已昏，渔梁渡头争渡喧。\n人随沙岸向江村，余亦乘舟归鹿门。\n鹿门月照开烟树，忽到庞公栖隐处。\n岩扉松径长寂寥，惟有幽人自来去。','唐诗三百首,写景,归隐',244,'2019-03-01 22:55:03'),(373,'苍梧谣·天','宋代','蔡伸','天。\n休使圆蟾照客眠。\n人何在，桂影自婵娟。','宋词三百首,宋词精选,月夜,思念',71,'2019-03-01 22:55:03'),(374,'国风·郑风·女曰鸡鸣','先秦','佚名','女曰鸡鸣，士曰昧旦。\n子兴视夜，明星有烂。\n将翱将翔，弋凫与雁。\n弋言加之，与子宜之。\n宜言饮酒，与子偕老。\n琴瑟在御，莫不静好。\n知子之来之，杂佩以赠之。\n知子之顺之，杂佩以问之。\n知子之好之，杂佩以报之。','诗经,爱情,民谣',243,'2019-03-01 22:55:03'),(375,'丰乐亭游春三首','宋代','欧阳修','绿树交加山鸟啼，晴风荡漾落花飞。\n鸟歌花舞太守醉，明日酒醒春已归。\n\n春云淡淡日辉辉，草惹行襟絮拂衣。\n行到亭西逢太守，篮舆酩酊插花归。\n\n红树青山日欲斜，长郊草色绿无涯。\n游人不管春将老，来往亭前踏落花。','春天,写景,惜春,抒怀',26,'2019-03-01 22:55:03'),(376,'念奴娇·西湖和人韵','宋代','辛弃疾','晚风吹雨，战新荷、声乱明珠苍璧。\n谁把香奁收宝镜，云锦红涵湖碧。\n飞鸟翻空，游鱼吹浪，惯趁笙歌席。\n坐中豪气，看公一饮千石。\n遥想处士风流，鹤随人去，老作飞仙伯。\n茅舍疏篱今在否，松竹已非畴昔。\n欲说当年，望湖楼下，水与云宽窄。\n醉中休问，断肠桃叶消息。','夏天,西湖,荷花,抒情,壮志难酬,悲愤',32,'2019-03-01 22:55:03'),(377,'归国谣·双脸','唐代','温庭筠','双脸，小凤战蓖金飐艳。\n舞衣无力风敛，藕丝秋色染。\n锦帐绣帷斜掩，露珠清晓簟，粉心黄蕊花靥，黛眉山两点。','写人,女子,抒情',3,'2019-03-01 22:55:03'),(378,'无题·万家墨面没蒿莱','近现代','鲁迅','万家墨面没蒿莱，敢有歌吟动地哀。\n心事浩茫连广宇，于无声处听惊雷。','战争,生活,向往,胜利',9,'2019-03-01 22:55:03'),(379,'花犯·谢黄复庵除夜寄古梅枝','宋代','吴文英','翦横枝，清溪分影，翛然镜空晓。\n小窗春到。\n怜夜冷孀娥，相伴孤照。\n古苔泪锁霜千点，苍华人共老。\n料浅雪、黄昏驿路，飞香遗冻草。\n 行云梦中认琼娘，冰肌瘦，窈窕风前纤缟。\n残醉醒，屏山外、翠禽声小。\n寒泉贮、绀壶渐暖，年事对、青灯惊换了。\n但恐舞、一帘胡蝶，玉龙吹又杳。','春节,咏物,梅花,抒情',19,'2019-03-01 22:55:03'),(380,'菩萨蛮·杏花含露团香雪','唐代','温庭筠','杏花含露团香雪，绿杨陌上多离别。\n灯在月胧明，觉来闻晓莺。\n玉钩褰翠幕，妆浅旧眉薄。\n春梦正关情，镜中蝉鬓轻。','写人,思妇,离情',3,'2019-03-01 22:55:03'),(381,'阳台梦·薄罗衫子金泥凤','唐代','李存勖','薄罗衫子金泥凤，困纤腰怯铢衣重。\n笑迎移步小兰丛，亸金翘玉凤。\n娇多情脉脉，羞把同心捻弄。\n楚天云雨却相和，又入阳台梦。','婉约,女子,思念',12,'2019-03-01 22:55:03'),(382,'诉衷情·宝月山作','宋代','仲殊','清波门外拥轻衣。\n杨花相送飞。\n西湖又还春晚，水树乱莺啼。\n闲院宇，小帘帏。\n晚初归。\n钟声已过，篆香才点，月到门时。','宋词三百首,写景',24,'2019-03-01 22:55:03'),(383,'渌水曲','唐代','李白','渌水明秋月，南湖采白蘋。\n荷花娇欲语，愁杀荡舟人。','乐府,荷花,女子',194,'2019-03-01 22:55:03'),(384,'浣溪沙·堤上游人逐画船','宋代','欧阳修','堤上游人逐画船，拍堤春水四垂天。\n绿杨楼外出秋千。\n白发戴花君莫笑，六幺催拍盏频传。\n人生何处似尊前！','春天,写景,宴饮',233,'2019-03-01 22:55:03'),(385,'陋室铭','唐代','刘禹锡','山不在高，有仙则名。\n水不在深，有龙则灵。\n斯是陋室，惟吾德馨。\n苔痕上阶绿，草色入帘青。\n谈笑有鸿儒，往来无白丁。\n可以调素琴，阅金经。\n无丝竹之乱耳，无案牍之劳形。\n南阳诸葛庐，西蜀子云亭。\n孔子云：何陋之有？','初中文言文,古文观止,咏物,赞美,品格',21871,'2019-03-01 22:55:03'),(386,'示金陵子','唐代','李白','金陵城东谁家子，窃听琴声碧窗里。\n落花一片天上来，随人直度西江水。\n楚歌吴语娇不成，似能未能最有情。\n谢公正要东山妓，携手林泉处处行。','写人,女子',7,'2019-03-01 22:55:03'),(387,'太常引·钱齐参议归山东','元代','刘燕哥','故人送我出阳关，无计锁雕鞍。\n今古别离难。\n兀谁画娥眉远山。\n一尊别酒，一声杜宇，寂寞又春残。\n明月小楼间，第一夜相思泪弹。','婉约,送别,惜别,相思',13,'2019-03-01 22:55:03'),(388,'幸蜀西至剑门','唐代','李隆基','剑阁横云峻，銮舆出狩回。\n翠屏千仞合，丹嶂五丁开。\n灌木萦旗转，仙云拂马来。\n乘时方在德，嗟尔勒铭才。','写景,议论',16,'2019-03-01 22:55:03'),(389,'十一月四日风雨大作二首','宋代','陆游','风卷江湖雨暗村，四山声作海涛翻。\n溪柴火软蛮毡暖，我与狸奴不出门。\n\n僵卧孤村不自哀，尚思为国戍轮台。\n夜阑卧听风吹雨，铁马冰河入梦来。','古诗三百首,初中古诗,爱国,壮志,最美,优美',4273,'2019-03-01 22:55:03'),(390,'赠傅都曹别','南北朝','鲍照','轻鸿戏江潭，孤雁集洲沚。\n邂逅两相亲，缘念共无已。\n风雨好东西，一隔顿万里。\n追忆栖宿时，声容满心耳。\n落日川渚寒，愁云绕天起。\n短翮不能翔，徘徊烟雾里。','赠别,友人,友谊,抒情,寂寞,别情',10,'2019-03-01 22:55:03'),(391,'清平乐·风鬟雨鬓','清代','纳兰性德','风鬟雨鬓，偏是来无准。\n倦倚玉兰看月晕，容易语低香近。\n软风吹遍窗纱，心期便隔天涯。\n从此伤春伤别，黄昏只对梨花。','爱情,思念',105,'2019-03-01 22:55:03'),(392,'关山月','唐代','李白','明月出天山，苍茫云海间。\n长风几万里，吹度玉门关。\n汉下白登道，胡窥青海湾。\n由来征战地，不见有人还。\n戍客望边邑，思归多苦颜。\n(望边邑 一作：望边色)高楼当此夜，叹息未应闲。','唐诗三百首,写山,边塞,军旅,思乡',2790,'2019-03-01 22:55:03'),(393,'关山月','南北朝','徐陵','关山三五月，客子忆秦川。\n思妇高楼上，当窗应未眠。\n星旗映疏勒，云阵上祁连。\n战气今如此，从军复几年。','古诗三百首,边塞,思念',570,'2019-03-01 22:55:03'),(394,'行香子·题罗浮','宋代','葛长庚','满洞苔钱。\n买断风烟。\n笑桃花流落晴川。\n石楼高处，夜夜啼猿。\n看二更云，三更月，四更天。\n细草如毡。\n独枕空拳。\n与山麋野鹿同眠。\n残霞未散，淡雾沈绵。\n是晋时人，唐时洞，汉时仙。','写景,自然,愉快',29,'2019-03-01 22:55:03'),(395,'扫花游·秋声','宋代','王沂孙','商飙乍发，渐淅淅初闻，萧萧还住。\n顿惊倦旅。\n背青灯吊影，起吟愁赋。\n断续无凭，试立荒庭听取。\n在何许？但落叶满阶，惟有高树。\n迢递归梦阻。\n正老耳难禁，病怀凄楚。\n故山院宇。\n想边鸿孤唳，砌蛩私语。\n数点相和，更著芭蕉细雨。\n避无处。\n这闲愁，夜深尤苦。','秋天,声音,写风,思乡',14,'2019-03-01 22:55:03'),(396,'行路难·其一','唐代','李白','金樽清酒斗十千，玉盘珍羞直万钱。\n(羞 通：馐；直 通：值)停杯投箸不能食，拔剑四顾心茫然。\n欲渡黄河冰塞川，将登太行雪满山。\n(雪满山 一作：雪暗天)闲来垂钓碧溪上，忽复乘舟梦日边。\n(碧 一作：坐)行路难！行路难！多歧路，今安在？长风破浪会有时，直挂云帆济沧海。','唐诗三百首,初中古诗,乐府,宴饮,怀才不遇,励志,哲理,最美,优美',8168,'2019-03-01 22:55:03'),(397,'江南弄','唐代','李贺','江中绿雾起凉波，天上叠巘红嵯峨。\n水风浦云生老竹，渚暝蒲帆如一幅。\n鲈鱼千头酒百斛，酒中倒卧南山绿。\n吴歈越吟未终曲，江上团团贴寒玉。','江南,写景,山水',15,'2019-03-01 22:55:03'),(398,'春怀示邻里','宋代','陈师道','断墙着雨蜗成字，老屋无僧燕作家，剩欲出门追语笑，却嫌归鬓著尘沙。\n风翻蛛网开三面，雷动蜂窠趁两衙。\n屡失南邻春事约，只今容有未开花。','古诗三百首,生活,惜春',41,'2019-03-01 22:55:03'),(399,'咏怀古迹五首·其一','唐代','杜甫','支离东北风尘际，漂泊西南天地间。\n三峡楼台淹日月，五溪衣服共云山。\n羯胡事主终无赖，词客哀时且未还。\n庾信平生最萧瑟，暮年诗赋动江关。','唐诗三百首,怀古,感叹',114,'2019-03-01 22:55:03'),(400,'生查子·落梅庭榭香','宋代','晏几道','落梅庭榭香，芳草池塘绿。\n春恨最关情，日过阑干曲。\n 几时花里闲，看得花枝足。\n醉后莫思家，借取师师宿。','春天,写景,思乡',18,'2019-03-01 22:55:03'),(401,'薄幸·青楼春晚','宋代','吕渭老','青楼春晚。\n昼寂寂、梳匀又懒。\n乍听得、鸦啼莺弄，惹起新愁无限。\n记年时、偷掷春心，花间隔雾遥相见。\n便角枕题诗，宝钗贳酒，共醉青苔深院。\n 怎忘得、回廊下，携手处、花明月满。\n如今但暮雨，蜂愁蝶恨，小窗闲对芭蕉展。\n却谁拘管。\n尽无言、闲品秦筝，泪满参差雁。\n腰支渐小，心与杨花共远。','婉约,爱情,思念,离情',22,'2019-03-01 22:55:03'),(402,'眼儿媚·酣酣日脚紫烟浮','宋代','范成大','萍乡道中乍晴，卧舆中困甚，小憩柳塘。\n\n酣酣日脚紫烟浮，妍暖破轻裘。\n困人天色，醉人花气，午梦扶头。\n春慵恰似春塘水，一片縠纹愁。\n溶溶泄泄，东风无力，欲皱还休。','婉约,春天,写景',26,'2019-03-01 22:55:03'),(403,'九日酬诸子','清代','妙信','不负东篱约，携尊过草堂。\n远天连树杪，高月薄衣裳。\n握手经年别，惊心九日霜。\n诸君才绝世，独步许谁强。','重阳节,饮酒,赏花,酬答,友人',8,'2019-03-01 22:55:03'),(404,'荷叶杯·镜水夜来秋月','唐代','温庭筠','镜水夜来秋月，如雪。\n采莲时，小娘红粉对寒浪。\n惆怅，正思惟。','秋天,月夜,少女,孤独,感伤',50,'2019-03-01 22:55:03'),(405,'八六子·洞房深','唐代','杜牧','洞房深，画屏灯照，山色凝翠沉沉。\n听夜雨冷滴芭蕉，惊断红窗好梦，龙烟细飘绣衾。\n辞恩久归长信，凤帐萧疏，椒殿闲扇。\n辇路苔侵。\n绣帘垂，迟迟漏传丹禁。\n舜华偷悴，翠鬟羞整，愁坐望处，金舆渐远，何时彩仗重临？正消魂，梧桐又移翠阴。','写人,宫怨,惆怅,孤寂',32,'2019-03-01 22:55:04'),(406,'寒食还陆浑别业','唐代','宋之问','洛阳城里花如雪，陆浑山中今始发。\n旦别河桥杨柳风，夕卧伊川桃李月。\n伊川桃李正芳新，寒食山中酒复春。\n野老不知尧舜力，酣歌一曲太平人。','寒食节,写景,抒情',15,'2019-03-01 22:55:04'),(407,'别匡山','唐代','李白','晓峰如画参差碧，藤影风摇拂槛垂。\n野径来多将犬伴，人间归晚带樵随。\n看云客倚啼猿树，洗钵僧临失鹤池。\n莫怪无心恋清境，已将书剑许明时。','写景,生活,抒怀,抱负,写狗',21,'2019-03-01 22:55:04'),(408,'南流夜郎寄内','唐代','李白','夜郎天外怨离居，明月楼中音信疏。\n北雁春归看欲尽，南来不得豫章书。','抒情,愁苦,思乡',7,'2019-03-01 22:55:04'),(409,'相见欢·花前顾影粼','清代','毛奇龄','花前顾影粼粼。\n水中人。\n水面残花片片绕人身。\n私自整，红斜领，茜儿巾。\n却讶领间巾里刺花新。','婉约,写花,写人',20,'2019-03-01 22:55:04'),(410,'南歌子·感旧','宋代','苏轼','寸恨谁云短，绵绵岂易裁。\n半年眉绿未曾开。\n明月好风闲处、是人猜。\n春雨消残冻，温风到冷灰。\n尊前一曲为谁哉。\n留取曲终一拍、待君来。','思念,妻子,抒情',53,'2019-03-01 22:55:04'),(411,'玉华宫','唐代','杜甫','贞观二十一年，作玉华宫，后改为寺，在宜君县北凤凰谷。\n\n溪回松风长，苍鼠窜古瓦。\n不知何王殿，遗构绝壁下。\n阴房鬼火青，坏道哀湍泻。\n万籁真笙竽，秋色正萧洒。\n美人为黄土，况乃粉黛假。\n当时侍金舆，故物独石马。\n忧来藉草坐，浩歌泪盈把。\n冉冉征途间，谁是长年者？','感慨,人生',5,'2019-03-01 22:55:04'),(412,'清江引·钱塘怀古','元代','任昱','吴山越山山下水，总是凄凉意。\n江流今古愁，山雨兴亡泪。\n沙鸥笑人闲未得。','山水,怀古',18,'2019-03-01 22:55:04'),(413,'国风·邶风·式微','先秦','佚名','式微，式微，胡不归？微君之故，胡为乎中露！式微，式微，胡不归？微君之躬，胡为乎泥中！','诗经',432,'2019-03-01 22:55:04'),(414,'殿前欢·大都西山','元代','唐毅夫','冷云间，夕阳楼外数峰闲。\n等闲不许俗人看，雨髻烟鬟。\n倚西风十二阑，休长叹。\n不多时暮霭风吹散，西山看我，我看西山。','写山,写景,人生',4,'2019-03-01 22:55:04'),(415,'舟中读元九诗','唐代','白居易','把君诗卷灯前读，诗尽灯残天未明。\n眼痛灭灯犹闇坐，逆风吹浪打船声。','关切,思念',46,'2019-03-01 22:55:04'),(416,'沉醉东风·赠妓朱帘秀','元代','胡祗遹','锦织江边翠竹，绒穿海上明珠。\n月淡时风清处，都隔断落红尘土。\n一片闲云任卷舒，挂尽朝云暮雨。','写人,赞美',4,'2019-03-01 22:55:04'),(417,'望鹦鹉洲怀祢衡','唐代','李白','魏帝营八极，蚁观一祢衡。\n黄祖斗筲人，杀之受恶名。\n吴江赋鹦鹉，落笔超群英。\n锵锵振金玉，句句欲飞鸣。\n鸷鹗啄孤凤，千春伤我情。\n五岳起方寸，隐然讵可平。\n才高竟何施，寡识冒天刑。\n至今芳洲上，兰蕙不忍生。','怀古',10,'2019-03-01 22:55:04'),(418,'蝃蝀','先秦','佚名','蝃蝀在东，莫之敢指。\n女子有行，远父母兄弟。\n朝隮于西，崇朝其雨。\n女子有行，远兄弟父母。\n乃如之人也，怀婚姻也。\n大无信也，不知命也！','诗经,讽刺',62,'2019-03-01 22:55:04'),(419,'戏赠郑溧阳','唐代','李白','陶令日日醉，不知五柳春。\n素琴本无弦，漉酒用葛巾。\n清风北窗下，自谓羲皇人。\n何时到栗里，一见平生亲。','写人,赞美,生活,闲适',20,'2019-03-01 22:55:04'),(420,'鹧鸪天·陌上柔桑破嫩芽','宋代','辛弃疾','陌上柔桑破嫩芽，东邻蚕种已生些。\n平冈细草鸣黄犊，斜日寒林点暮鸦。\n山远近，路横斜，青旗沽酒有人家。\n城中桃李愁风雨，春在溪头荠菜花。','早春,乡村,忧国忧民,写景',282,'2019-03-01 22:55:04'),(421,'乞巧','唐代','林杰','七夕今宵看碧霄，牵牛织女渡河桥。\n 家家乞巧望秋月，穿尽红丝几万条。','小学古诗,七夕节',1236,'2019-03-01 22:55:04'),(422,'咏牡丹','宋代','王溥','枣花至小能成实，桑叶虽柔解吐丝。\n堪笑牡丹如斗大，不成一事又空枝。','咏物,写花,抒怀',114,'2019-03-01 22:55:04'),(423,'山人劝酒','唐代','李白','苍苍云松，落落绮皓。\n春风尔来为阿谁，蝴蝶忽然满芳草。\n秀眉霜雪颜桃花，骨青髓绿长美好。\n称是秦时避世人，劝酒相欢不知老。\n各守麋鹿志，耻随龙虎争。\n欻起佐太子，汉王乃复惊。\n顾谓戚夫人，彼翁羽翼成。\n归来商山下，泛若云无情。\n举觞酹巢由，洗耳何独清。\n浩歌望嵩岳，意气还相倾。','乐府,仕途,感伤',23,'2019-03-01 22:55:04'),(424,'听角思归','唐代','顾况','故园黄叶满青苔，梦破城头晓角哀。\n此夜断肠人不见，起行残月影徘徊。','思念,家乡',30,'2019-03-01 22:55:04'),(425,'咏蝉 / 在狱咏蝉','唐代','骆宾王','西陆蝉声唱，南冠客思深。\n(客思深 一作：客思侵)不堪玄鬓影，来对白头吟。\n(不堪 一作：那堪)露重飞难进，风多响易沉。\n无人信高洁，谁为表予心？','唐诗三百首,咏物',450,'2019-03-01 22:55:04'),(426,'战城南','南北朝','何承天','战城南，冲黄尘，丹旌电烻鼓雷震。\n勍敌猛，戎马殷，横阵亘野若屯云。\n仗大顺，应三灵，义之所感士忘生。\n长剑击，繁弱鸣，飞镝炫晃乱奔星。\n虎骑跃，华眊旋，朱火延起腾飞烟。\n骁雄斩，高旗搴，长角浮叫响清天。\n夷群寇，殪逆徒，馀黎落惠咏来苏。\n奏恺乐，归皇都，班爵献俘邦国娱。','宋词三百首,人生,感悟',9,'2019-03-01 22:55:04'),(427,'花非花','唐代','白居易','花非花，雾非雾。\n夜半来，天明去。\n来如春梦几多时？去似朝云无觅处。','婉约,写马,感慨',1186,'2019-03-01 22:55:04'),(428,'清平乐·平原放马','宋代','张炎','辔摇衔铁。\n蹴踏平原雪。\n勇趁军声曾汗血。\n闲过升平时节。\n 茸茸春草天涯。\n涓涓野水晴沙。\n多少骅骝老去，至今犹困盐车。','写景,抒情',23,'2019-03-01 22:55:04'),(429,'少年游·端午赠黄守徐君猷','宋代','苏轼','银塘朱槛麹尘波，圆绿卷新荷。\n兰条荐浴，菖花酿酒，天气尚清和。\n好将沉醉酬佳节，十分酒、一分歌。\n狱草烟深，讼庭人悄，无吝宴游过。','端午,写景,抒情,爱国',15,'2019-03-01 22:55:04'),(430,'利州南渡','唐代','温庭筠','澹然空水对斜晖，曲岛苍茫接翠微。\n波上马嘶看棹去，柳边人歇待船归。\n数丛沙草群鸥散，万顷江田一鹭飞。\n谁解乘舟寻范蠡，五湖烟水独忘机。','唐诗三百首,渡江,山水,抒怀,写景',69,'2019-03-01 22:55:04'),(431,'菩萨蛮·溪山掩映斜阳里','宋代','魏夫人','溪山掩映斜阳里，楼台影动鸳鸯起。\n隔岸两三家，出墙红杏花。\n绿杨堤下路，早晚溪边去。\n三见柳绵飞，离人犹未归。','婉约,写景,妇女,怀远,离情',26,'2019-03-01 22:55:04'),(432,'堂成','唐代','杜甫','背郭堂成荫白茅，缘江路熟俯青郊。\n桤林碍日吟风叶，笼竹和烟滴露梢。\n暂止飞乌将数子，频来语燕定新巢。\n旁人错比扬雄宅，懒惰无心作解嘲。','写景,抒情',26,'2019-03-01 22:55:04'),(433,'竹马子·登孤垒荒凉','宋代','柳永','登孤垒荒凉，危亭旷望，静临烟渚。\n对雌霓挂雨，雄风拂槛，微收烦暑。\n渐觉一叶惊秋，残蝉噪晚，素商时序。\n览景想前欢，指神京，非雾非烟深处。\n向此成追感，新愁易积，故人难聚。\n凭高尽日凝伫。\n赢得消魂无语。\n极目霁霭霏微，瞑鸦零乱，萧索江城暮。\n南楼画角，又送残阳去。','羁旅,抒怀,感慨,孤寂,愁苦,秋思',16,'2019-03-01 22:55:04'),(434,'春日归山寄孟浩然','唐代','李白','朱绂遗尘境，青山谒梵筵。\n金绳开觉路，宝筏度迷川。\n岭树攒飞栱，岩花覆谷泉。\n塔形标海月，楼势出江烟。\n香气三天下，钟声万壑连。\n荷秋珠已满，松密盖初圆。\n鸟聚疑闻法，龙参若护禅。\n愧非流水韵，叨入伯牙弦。','春天,写景,赞美,友人',24,'2019-03-01 22:55:04'),(435,'葛覃','先秦','佚名','葛之覃兮，施于中谷，维叶萋萋。\n黄鸟于飞，集于灌木，其鸣喈喈。\n葛之覃兮，施于中谷，维叶莫莫。\n是刈是濩，为絺为綌，服之无斁。\n言告师氏，言告言归。\n薄污我私，薄浣我衣。\n害浣害否？归宁父母。','诗经,写人',1972,'2019-03-01 22:55:04'),(436,'赋得江边柳','唐代','鱼玄机','翠色连荒岸，烟姿入远楼。\n影铺秋水面，花落钓人头。\n根老藏鱼窟，枝低系客舟。\n萧萧风雨夜，惊梦复添愁。','柳树,写景,抒情,愁绪',75,'2019-03-01 22:55:04'),(437,'咏利城山居','魏晋','支遁','五岳盘神基，四渎涌荡津。\n动求目方智，默守标静仁。\n苟不宴出处，托好有常因。\n寻元存终古，洞往想逸民。\n玉洁箕岩下，金声濑沂滨。\n卷华藏纷雾，振褐拂埃尘。\n迹从尺蠖曲，道与腾龙伸。\n峻无单豹伐，分非首阳真。\n长啸归林岭，潇洒任陶钧。','隐居,生活',9,'2019-03-01 22:55:04'),(438,'曾参烹彘','先秦','韩非','曾子之妻之市，其子随之而泣。\n其母曰：“女还，顾反为女杀彘。\n”妻适市来，曾子欲捕彘杀之。\n妻止之曰：“特与婴儿戏耳。\n”曾子曰：“婴儿非与戏也。\n婴儿非有知也，待父母而学者也，听父母之教。\n今子欺之，是教子欺也。\n母欺子，子而不信其母，非所以成教也。\n”遂烹彘也。\n　　（选自《韩非子．外储说左上》）','教育,哲理',92,'2019-03-01 22:55:04'),(439,'湘月·天风吹我','清代','龚自珍','壬申夏泛舟西湖，述怀有赋，时予别杭州盖十年矣\n天风吹我，堕湖山一角，果然清丽。\n曾是东华生小客，回首苍茫无际。\n屠狗功名，雕龙文卷，岂是平生意？乡亲苏小，定应笑我非计。\n才见一抹斜阳，半堤香草，顿惹清愁起。\n罗袜音尘何处觅，渺渺予怀孤寄。\n怨去吹箫，狂来说剑，两样销魂味。\n两般春梦，橹声荡入云水。','豪放,写景,抒怀',19,'2019-03-01 22:55:04'),(440,'蝶恋花·梦入江南烟水路','宋代','晏几道','梦入江南烟水路，行尽江南，不与离人遇。\n睡里消魂无说处，觉来惆怅消魂误。\n欲尽此情书尺素，浮雁沉鱼，终了无凭据。\n却倚缓弦歌别绪，断肠移破秦筝柱。','宋词精选,闺怨,叙梦,相思,离情',204,'2019-03-01 22:55:04'),(441,'浣溪沙·琴川慧日寺蜡梅','宋代','吴文英','蝶粉蜂黄大小乔。\n中庭寒尽雪微销。\n一般清瘦各无聊。\n 窗下和香封远讯，墙头飞玉怨邻箫。\n夜来风雨洗春娇。','咏物,腊梅,思念',11,'2019-03-01 22:55:04'),(442,'嫦娥','唐代','李商隐','云母屏风烛影深，长河渐落晓星沉。\n嫦娥应悔偷灵药，碧海青天夜夜心。','唐诗三百首,小学古诗,写人,孤寂',8700,'2019-03-01 22:55:04'),(443,'东门之杨','先秦','佚名','东门之杨，其叶牂牂。\n昏以为期，明星煌煌。\n东门之杨，其叶肺肺。\n昏以为期，明星晢晢。','诗经,爱情',69,'2019-03-01 22:55:04'),(444,'河满子·正是破瓜年纪','五代','和凝','正是破瓜年纪，含情惯得人饶。\n桃李精神鹦鹉舌，可堪虚度良宵。\n却爱蓝罗裙子，羡他长束纤腰。','婉约,爱慕,写人,女子',15,'2019-03-01 22:55:04'),(445,'青门柳','唐代','白居易','青青一树伤心色，曾入几人离恨中。\n 为近都门多送别，长条折尽减春风。','赠别,伤春',213,'2019-03-01 22:55:05'),(446,'九日水阁','宋代','韩琦','池馆隳摧古榭荒，此延嘉客会重阳。\n虽惭老圃秋容淡，且看黄花晚节香。\n酒味已醇新过熟，蟹螯先实不须霜。\n年来饮兴衰难强，漫有高吟力尚狂。','重阳节,宴饮,赏花,赞美,品格',34,'2019-03-01 22:55:05'),(447,'清平乐·检校山园书所见','宋代','辛弃疾','连云松竹，万事从今足。\n拄杖东家分社肉，白酒床头初熟。\n西风梨枣山园，儿童偷把长竿。\n莫遣旁人惊去，老夫静处闲看。','农村,生活',51,'2019-03-01 22:55:05'),(448,'望月有感','唐代','白居易','自河南经乱，关内阻饥，兄弟离散，各在一处。\n因望月有感，聊书所怀，寄上浮梁大兄、於潜七兄、乌江十五兄，兼示符离及下邽弟妹。\n\n时难年荒世业空，弟兄羁旅各西东。\n田园寥落干戈后，骨肉流离道路中。\n吊影分为千里雁，辞根散作九秋蓬。\n共看明月应垂泪，一夜乡心五处同。','唐诗三百首,初中古诗,望月,抒情,思乡',1531,'2019-03-01 22:55:05'),(449,'早朝大明宫呈两省僚友','唐代','贾至','银烛朝天紫陌长，禁城春色晓苍苍。\n千条弱柳垂青琐，百啭流莺满建章。\n剑佩声随玉墀步，衣冠身惹御炉香。\n共沐恩波凤池上，朝朝染翰侍君王。','春天,写景,宫中',26,'2019-03-01 22:55:05'),(450,'舟夜书所见','清代','查慎行','月黑见渔灯，孤光一点萤。\n微微风簇浪，散作满河星。','古诗三百首,写景',526,'2019-03-01 22:55:05'),(451,'浪淘沙·极目楚天空','宋代','幼卿','极目楚天空，云雨无踪，漫留遗恨锁眉峰。\n自是荷花开较晚，孤负东风。\n客馆叹飘蓬，聚散匆匆，扬鞭那忍骤花骢。\n望断斜阳人不见，满袖啼红。','婉约,写景,抒情',27,'2019-03-01 22:55:05'),(452,'秋凉晚步','宋代','杨万里','秋气堪悲未必然，轻寒正是可人天。\n绿池落尽红蕖却，荷叶犹开最小钱。','秋天,写景,抒怀',245,'2019-03-01 22:55:05'),(453,'四和香·麦浪翻晴风飐柳','清代','纳兰性德','麦浪翻晴风飐柳，已过伤春候。\n因甚为他成僝僽？毕竟是春迤逗。\n红药阑边携素手，暖语浓于酒。\n盼到园花铺似绣，却更比春前瘦。','写景,伤春',16,'2019-03-01 22:55:05'),(454,'阮郎归·南园春半踏青时','宋代','欧阳修','南园春半踏青时，风和闻马嘶。\n青梅如豆柳如眉，日长蝴蝶飞。\n花露重，草烟低，人家帘幕垂。\n秋千慵困解罗衣，画堂双燕归。','宋词精选,婉约,妇女,春游,写景',109,'2019-03-01 22:55:05'),(455,'登峨眉山','唐代','李白','蜀国多仙山，峨眉邈难匹。\n周流试登览，绝怪安可悉？青冥倚天开，彩错疑画出。\n泠然紫霞赏，果得锦囊术。\n云间吟琼箫，石上弄宝瑟。\n平生有微尚，欢笑自此毕。\n烟容如在颜，尘累忽相失。\n倘逢骑羊子，携手凌白日。','写山',43,'2019-03-01 22:55:05'),(456,'送当涂赵少府赴长芦','唐代','李白','我来扬都市，送客回轻舠。\n因夸楚太子，便观广陵涛。\n仙尉赵家玉，英风凌四豪。\n维舟至长芦，目送烟云高。\n摇扇对酒楼，持袂把蟹螯。\n前途倘相思，登岳一长谣。','送别,友人,抒情',8,'2019-03-01 22:55:05'),(457,'咏燕 / 归燕诗','唐代','张九龄','海燕虽微渺，乘春亦暂来。\n岂知泥滓贱，只见玉堂开。\n绣户时双入，华堂日几回。\n无心与物竞，鹰隼莫相猜。','托物言志',17,'2019-03-01 22:55:05'),(458,'鱼藻','先秦','佚名','鱼在在藻，有颁其首。\n王在在镐，岂乐饮酒。\n\n鱼在在藻，有莘其尾。\n王在在镐，饮酒乐岂。\n\n鱼在在藻，依于其蒲。\n王在在镐，有那其居。','诗经,咏物,写鱼',46,'2019-03-01 22:55:05'),(459,'送白少府送兵之陇右','唐代','高适','践更登陇首，远别指临洮。\n为问关山事，何如州县劳。\n军容随赤羽，树色引青袍。\n谁断单于臂，今年太白高。','军队,战争',21,'2019-03-01 22:55:05'),(460,'乱后逢村叟','唐代','杜荀鹤','经乱衰翁居破村，村中何事不伤魂。\n因供寨木无桑柘，为著乡兵绝子孙。\n还似平宁征赋税，未尝州县略安存。\n至今鸡犬皆星散，日落前山独倚门。','战争,困苦,同情',3,'2019-03-01 22:55:05'),(461,'天仙子·月落城乌啼未了','清代','纳兰性德','月落城乌啼未了，起来翻为无眠早。\n薄霜庭院怯生衣，心悄悄，红阑绕。\n此情待共谁人晓？','抒情,忧愁,孤寂',12,'2019-03-01 22:55:05'),(462,'祝英台近·晚春','宋代','辛弃疾','宝钗分，桃叶渡，烟柳暗南浦。\n怕上层楼，十日九风雨。\n断肠片片飞红，都无人管，更谁劝、啼莺声住？鬓边觑，试把花卜归期，才簪又重数。\n罗帐灯昏，哽咽梦中语：是他春带愁来，春归何处？却不解、带将愁去。','宋词精选,婉约,闺怨,女子,怀远,离情',71,'2019-03-01 22:55:05'),(463,'卜算子慢·江枫渐老','宋代','柳永','江枫渐老，汀蕙半凋，满目败红衰翠。\n楚客登临，正是暮秋天气。\n引疏砧、断续残阳里。\n对晚景、伤怀念远，新愁旧恨相继。\n 脉脉人千里。\n念两处风情，万重烟水。\n雨歇天高，望断翠峰十二。\n侭无言、谁会凭高意。\n纵写得、离肠万种，奈归云谁寄。','伤怀,孤独,凄凉,离情',30,'2019-03-01 22:55:05'),(464,'寄宇文判官','唐代','岑参','西行殊未已，东望何时还。\n终日风与雪，连天沙复山。\n二年领公事，两度过阳关。\n相忆不可见，别来头已斑。','抒情,思乡,愁苦',10,'2019-03-01 22:55:05'),(465,'百字令·月夜过七里滩','清代','厉鹗','月夜过七里滩，光景奇绝。\n歌此调，几令众山皆响。\n\n秋光今夜，向桐江，为写当年高躅。\n风露皆非人世有，自坐船头吹竹。\n万籁生山，一星在水，鹤梦疑重续。\n挐音遥去，西岩渔父初宿。\n心忆汐社沉埋，清狂不见，使我形容独。\n寂寂冷萤三四点，穿破前湾茅屋。\n林净藏烟，峰危限月，帆影摇空绿。\n随风飘荡，白云还卧深谷。','秋夜,怀人,孤独,写景',11,'2019-03-01 22:55:05'),(466,'马上作','明代','戚继光','南北驱驰报主情，江花边草笑平生。\n(边草 一作：边月)一年三百六十日，多是横戈马上行。\n (多是 一作：都是)','古诗三百首,战争,英雄',385,'2019-03-01 22:55:05'),(467,'小重山·三山与客泛西湖','宋代','辛弃疾','绿涨连云翠拂空。\n十分风月处，著衰翁。\n垂杨影断岸西东。\n君恩重，教且种芙蓉。\n十里水晶宫。\n有时骑马去，笑儿童。\n殷勤却谢打头风。\n船儿住，且醉浪花中。','西湖,写景,抒情,伤怀',6,'2019-03-01 22:55:05'),(468,'谒金门·美人浴','五代','阎选','美人浴，碧沼莲开芬馥。\n双髻绾云颜似玉，素蛾辉淡绿。\n雅态芳姿闲淑，雪映钿装金斛。\n水溅青丝珠断续，酥融香透肉。','写人,女子',3,'2019-03-01 22:55:05'),(469,'齐安郡中偶题二首·其一','唐代','杜牧','两竿落日溪桥上，半缕轻烟柳影中。\n多少绿荷相倚恨，一时回首背西风。','写景,抒情',55,'2019-03-01 22:55:05'),(470,'生查子·官身几日闲','宋代','晏几道','官身几日闲，世事何时足。\n君貌不常红，我鬓无重绿。\n榴花满盏香，金缕多情曲。\n且尽眼中欢，莫叹时光促。','抒情,怀才不遇,感慨,生活',8,'2019-03-01 22:55:05'),(471,'菩萨蛮·梨花满院飘香雪','五代','毛熙震','梨花满院飘香雪，高楼夜静风筝咽。\n斜月照帘帷，忆君和梦稀。\n小窗灯影背，燕语惊愁态。\n屏掩断香飞，行云山外归。','写景,思念',33,'2019-03-01 22:55:05'),(472,'他乡七夕','唐代','孟浩然','他乡逢七夕，旅馆益羁愁。\n不见穿针妇，空怀故国楼。\n绪风初减热，新月始临秋。\n谁忍窥河汉，迢迢问斗牛。','七夕节,思乡',34,'2019-03-01 22:55:05'),(473,'水龙吟·过秋壑湖上旧居寄赠','宋代','吴文英','外湖北岭云多，小园暗碧莺啼处。\n朝回胜赏，墨池香润，吟船系雨。\n霓节千妃，锦帆一箭，携将春去。\n算归期未卜，青烟散后，春城咏、飞花句。\n 黄鹤楼头月午。\n奏玉龙、江梅解舞。\n薰风紫禁，严更清梦，思怀几许。\n秋水生时，赋情还在，南屏别墅。\n看章台走马，长堤种取，柔丝千树。','抒怀,志向,怀才不遇',4,'2019-03-01 22:55:05'),(474,'有所思','唐代','李白','我思仙人，乃在碧海之东隅。\n 海寒多天风，白波连山倒蓬壶。\n长鲸喷涌不可涉，抚心茫茫泪如珠。\n西来青鸟东飞去，愿寄一书谢麻姑。','闲适,生活',28,'2019-03-01 22:55:05'),(475,'菩萨蛮·数间茅屋闲临水','宋代','王安石','数间茅屋闲临水，窄衫短帽垂杨里。\n花是去年红，吹开一夜风。\n梢梢新月偃，午醉醒来晚。\n何物最关情，黄鹂三两声。','神话,想象',82,'2019-03-01 22:55:05'),(476,'天上谣','唐代','李贺','天河夜转漂回星，银浦流云学水声。\n玉宫桂树花未落，仙妾采香垂佩缨。\n秦妃卷帘北窗晓，窗前植桐青凤小。\n王子吹笙鹅管长，呼龙耕烟种瑶草。\n粉霞红绶藕丝裙，青洲步拾兰苕春。\n东指羲和能走马，海尘新生石山下。','赠别,写人,友人,赞美',101,'2019-03-01 22:55:05'),(477,'赋得还山吟送沈四山人','唐代','高适','还山吟，天高日暮寒山深，送君还山识君心。\n人生老大须恣意，看君解作一生事。\n山间偃仰无不至，石泉淙淙若风雨，桂花松子常满地。\n卖药囊中应有钱，还山服药又长年。\n白云劝尽杯中物，明月相随何处眠？眠时忆问醒时事，梦魂可以相周旋。','写景,抒情,乐观',14,'2019-03-01 22:55:05'),(478,'浣溪沙·红蓼花香夹岸稠','宋代','晏殊','红蓼花香夹岸稠，绿波春水向东流。\n小船轻舫好追游。\n渔父酒醒重拨棹，鸳鸯飞去却回头。\n一杯销尽两眉愁。','春天,写水,写景,赞美',12,'2019-03-01 22:55:05'),(479,'东平别前卫县李寀少府 / 送前卫县李宷少府','唐代','高适','黄鸟翩翩杨柳垂，春风送客使人悲。\n怨别自惊千里外，论交却忆十年时。\n云开汶水孤帆远，路绕梁山匹马迟。\n此地从来可乘兴，留君不住益凄其。','送别,惜别,友情',16,'2019-03-01 22:55:05'),(480,'题三义塔','近现代','鲁迅','三义塔者，中国上海闸北三义里遗鸠埋骨之塔也，在日本，农人共建。\n\n奔霆飞熛歼人子，败井颓垣剩饿鸠。\n 偶值大心离火宅，终遗高塔念瀛洲。\n精禽梦觉仍衔石，斗士诚坚共抗流。\n度尽劫波兄弟在，相逢一笑泯恩仇。\n西村博士于上海战后得丧家之鸠，持归养之，初亦相安，而终化去。\n建塔以藏，且征题咏，率成一律，聊答遐情云尔。','咏物,抒情,议论,愿望',21,'2019-03-01 22:55:06'),(481,'浪淘沙·小绿间长红','宋代','晏几道','小绿间长红，露蕊烟丛。\n花开花落昔年同。\n惟恨花前携手处，往事成空。\n山远水重重，一笑难逢。\n已拚长在别离中。\n霜鬓知他从此去，几度春风。','春天,女子,离别,伤怀,愁绪',54,'2019-03-01 22:55:06'),(482,'玉壶吟','唐代','李白','烈士击玉壶，壮心惜暮年。\n三杯拂剑舞秋月，忽然高咏涕泗涟。\n凤凰初下紫泥诏，谒帝称觞登御筵。\n揄扬九重万乘主，谑浪赤墀青琐贤。\n朝天数换飞龙马，敕赐珊瑚白玉鞭。\n世人不识东方朔，大隐金门是谪仙。\n西施宜笑复宜颦，丑女效之徒累身。\n君王虽爱蛾眉好，无奈宫中妒杀人！','奔放,写人,壮志',30,'2019-03-01 22:55:06'),(483,'谪居粮绝请学于农将田南山咏言寄怀','明代','王守仁','谪居履在陈，从者有温见。\n山荒聊可田，钱镈还易办。\n夷俗多火耕，仿习亦颇便。\n及兹春未深，数亩犹足佃。\n岂徒实口腹，且以理荒宴。\n遗穗及乌雀，贫寡发余羡。\n出耒在明晨，山寒易霜霰。','学习,农事,抒情',12,'2019-03-01 22:55:06'),(484,'鹧鸪天·绿橘梢头几点春','宋代','晏几道','绿橘梢头几点春。\n似留香蕊送行人。\n明朝紫凤朝天路，十二重城五碧云。\n歌渐咽，酒初醺。\n尽将红泪湿湘裙。\n赣江西畔从今日，明月清风忆使君。','写景,送别,不舍',9,'2019-03-01 22:55:06'),(485,'最高楼·吾衰矣','宋代','辛弃疾','吾拟乞归，犬子以田产未置止我，赋此骂之。\n\n吾衰矣，须富贵何时？富贵是危机。\n暂忘设醴抽身去，未曾得米弃官归。\n穆先生，陶县令，是吾师。\n待葺个园儿名“佚老”，更作个亭儿名“亦好”，闲饮酒，醉吟诗。\n千年田换八百主，一人口插几张匙？便休休，更说甚，是和非！','想象,生活,归隐',40,'2019-03-01 22:55:06'),(486,'酬程延秋夜即事见赠','唐代','韩翃','长簟迎风早，空城澹月华。\n星河秋一雁，砧杵夜千家。\n 节候看应晚，心期卧亦赊。\n向来吟秀句，不觉已鸣鸦。','唐诗三百首,酬答,写景',44,'2019-03-01 22:55:06'),(487,'渭川田家','唐代','王维','斜阳照墟落，穷巷牛羊归。\n(斜阳 一作：斜光)野老念牧童，倚杖候荆扉。\n雉雊麦苗秀，蚕眠桑叶稀。\n田夫荷锄至，相见语依依。\n即此羡闲逸，怅然吟式微。','唐诗三百首,田园,归隐',242,'2019-03-01 22:55:06'),(488,'蝶恋花·窗外绿阴添几许','近现代','王国维','窗外绿阴添几许。\n剩有朱樱，尚系残春住。\n老尽莺雏无一语。\n飞来衔得樱桃去。\n坐看画梁双燕乳。\n燕语呢喃，似惜人迟暮。\n自是思量渠不与。\n人间总被思量误。','伤春,怨别',41,'2019-03-01 22:55:06'),(489,'阮郎归·柳阴庭院占风光','宋代','曾觌','柳阴庭院占风光，呢喃清昼长。\n碧波新涨小池塘，双双蹴水忙。\n萍散漫，絮飘飏，轻盈体态狂。\n为怜流去落红香，衔将归画梁。','婉约,写鸟,惜花,寓人',26,'2019-03-01 22:55:06'),(490,'一箧磨穴砚','未知','佚名','古人有学书于人者，自以为艺成，辞而去。\n师曰：“吾有一箧物，不欲付他人，愿托置于某山下。\n”其人受之，因其封题不甚密，乃启而视之，皆磨穴之砚也，数十枚，方知师夙用者。\n顿觉羞愧，及反而学，至精其艺。','初中文言文,寓理,教育,故事',37,'2019-03-01 22:55:06'),(491,'清平乐·候蛩凄断','宋代','张炎','候蛩凄断，人语西风岸。\n月落沙平江似练，望尽芦花无雁。\n暗教愁损兰成，可怜夜夜关情。\n只有一枝梧叶，不知多少秋声！','宋词三百首,秋天,感伤',104,'2019-03-01 22:55:06'),(492,'晚泊岳阳','宋代','欧阳修','卧闻岳阳城里钟，系舟岳阳城下树。\n正见空江明月来，云水苍茫失江路。\n夜深江月弄清辉，水上人歌月下归；一阕声长听不尽，轻舟短楫去如飞。','古诗三百首,贬谪,写景,思归',53,'2019-03-01 22:55:06'),(493,'琴诗','宋代','苏轼','若言琴上有琴声，放在匣中何不鸣？若言声在指头上，何不于君指上听？','美学,哲理',366,'2019-03-01 22:55:06'),(494,'得献吉江西书','明代','何景明','近得浔阳江上书，遥思李白更愁予。\n天边魑魅窥人过，日暮鼋鼍傍客居。\n鼓柁湘江应未得，买田阳羡定何如？他年淮水能相访，桐柏山中共结庐。','赠友,友谊,抒情',2,'2019-03-01 22:55:06'),(495,'红芍药·人生百岁','宋代','王观','人生百岁，七十稀少。\n更除十年孩童小。\n又十年昏老。\n都来五十载，一半被、睡魔分了。\n那二十五载之中，宁无些个烦恼。\n仔细思量，好追欢及早。\n遇酒追朋笑傲。\n任玉山摧倒。\n沈醉且沈醉，人生似、露垂芳草。\n幸新来、有酒如渑，结千秋歌笑。','感慨,人生,时间,抒怀',35,'2019-03-01 22:55:06'),(496,'灞岸','唐代','李商隐','山东今岁点行频，几处冤魂哭虏尘。\n灞水桥边倚华表，平时二月有东巡。','抒怀,战争,感慨,同情',2,'2019-03-01 22:55:06'),(497,'旅宿','唐代','杜牧','旅馆无良伴，凝情自悄然。\n寒灯思旧事，断雁警愁眠。\n远梦归侵晓，家书到隔年。\n沧江好烟月，门系钓鱼船。','唐诗三百首,羁旅,思乡',137,'2019-03-01 22:55:06'),(498,'瑞鹤仙·脸霞红印枕','宋代','陆淞','脸霞红印枕，睡觉来、冠儿还是不整。\n屏间麝煤冷，但眉峰压翠，泪珠弹粉。\n堂深昼永，燕交飞、风帘露井。\n恨无人说与，相思近日，带围宽尽。\n重省，残灯朱幌，淡月纱窗，那时风景。\n阳台路迥，云雨梦，便无准。\n待归来，先指花梢教看，欲把心期细问。\n问因循过了青春，怎生意稳？','闺怨,妇女,回忆,伤怀',20,'2019-03-01 22:55:06');
